import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [totalStudents, setTotalStudents] = useState(0);
  const [vaccinatedCount, setVaccinatedCount] = useState(0);
  const [drives, setDrives] = useState([]);

  // Fetch data logic combined in a single async function
  const fetchData = async () => {
    try {
      const [studentsRes, drivesRes] = await Promise.all([
        axios.get('http://localhost:5000/students'),
        axios.get('http://localhost:5000/vaccination-drives', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }),
      ]);

      const students = studentsRes.data;
      const vaccinated = students.filter(s => s.vaccinationStatus === 'Vaccinated');

      setTotalStudents(students.length);
      setVaccinatedCount(vaccinated.length);

      const sortedDrives = drivesRes.data.sort((a, b) => new Date(a.date) - new Date(b.date));
      setDrives(sortedDrives);
    } catch (err) {
      console.error('Error fetching data:', err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const vaccinatedPercentage = totalStudents ? ((vaccinatedCount / totalStudents) * 100).toFixed(2) : 0;

  // Utility function for rendering stat cards
  const renderStatCard = (label, value) => (
    <div className="col-md-4 mb-3">
      <div className="card p-3 text-center bg-light">
        <h5>{label}</h5>
        <h3>{value}</h3>
      </div>
    </div>
  );

  // Utility function for rendering vaccination drive rows
  const renderDriveRows = () => {
    if (drives.length === 0) {
      return (
        <tr>
          <td colSpan="4" className="text-center">No drives scheduled</td>
        </tr>
      );
    }

    return drives.map(drive => (
      <tr key={drive.id}>
        <td>{drive.vaccine}</td>
        <td>{drive.date}</td>
        <td>{drive.doses}</td>
        <td>{drive.classes}</td>
      </tr>
    ));
  };

  return (
    <div className="container mt-4">
      <h2>Dashboard</h2>

      <div className="row mt-4">
        {renderStatCard('Total Students', totalStudents)}
        {renderStatCard('Vaccinated Students', vaccinatedCount)}
        {renderStatCard('Vaccination %', `${vaccinatedPercentage}%`)}
      </div>

      <div className="card mt-5 p-3">
        <h4>Upcoming Vaccination Drives</h4>
        <table className="table table-bordered mt-3">
          <thead>
            <tr>
              <th>Vaccine</th>
              <th>Date</th>
              <th>Doses</th>
              <th>Classes</th>
            </tr>
          </thead>
          <tbody>
            {renderDriveRows()}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
