import React from 'react';

const AddStudentForm = ({ newStudent, setNewStudent, handleAddStudent }) => (
  <div className="mb-8 border p-6 rounded-lg shadow-lg bg-white">
    <h2 className="text-2xl font-semibold mb-6 text-primary">Add New Student</h2>
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      <input
        placeholder="Name"
        value={newStudent.name}
        onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })}
        className="border p-3 rounded-lg w-full focus:ring-2 focus:ring-primary transition-all"
      />
      <input
        placeholder="Class"
        value={newStudent.class}
        onChange={(e) => setNewStudent({ ...newStudent, class: e.target.value })}
        className="border p-3 rounded-lg w-full focus:ring-2 focus:ring-primary transition-all"
      />
      <input
        placeholder="Student ID"
        value={newStudent.studentId}
        onChange={(e) => setNewStudent({ ...newStudent, studentId: e.target.value })}
        className="border p-3 rounded-lg w-full focus:ring-2 focus:ring-primary transition-all"
      />
    </div>
    <button
      onClick={handleAddStudent}
      className="btn mt-6 w-full md:w-auto py-2 px-4 rounded-lg bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 text-white font-semibold hover:opacity-90 transition-all"
    >
      Add Student
    </button>
  </div>
);

export default AddStudentForm;
