import React from 'react';

const AddStudentForm = ({ newStudent, setNewStudent, handleAddStudent }) => (
  <div className="mb-8 p-6 rounded-lg shadow-lg bg-gradient-to-r from-teal-400 via-green-500 to-blue-600 text-white">
    <h2 className="text-2xl font-semibold mb-6 text-center">Add New Student</h2>
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
      <input
        placeholder="Name"
        value={newStudent.name}
        onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })}
        className="p-3 rounded-lg text-black focus:outline-none focus:ring-2 focus:ring-teal-300"
      />
      <input
        placeholder="Class"
        value={newStudent.class}
        onChange={(e) => setNewStudent({ ...newStudent, class: e.target.value })}
        className="p-3 rounded-lg text-black focus:outline-none focus:ring-2 focus:ring-teal-300"
      />
      <input
        placeholder="Student ID"
        value={newStudent.studentId}
        onChange={(e) => setNewStudent({ ...newStudent, studentId: e.target.value })}
        className="p-3 rounded-lg text-black focus:outline-none focus:ring-2 focus:ring-teal-300"
      />
    </div>
    <div className="flex justify-center mt-6">
      <button
        onClick={handleAddStudent}
        className="px-8 py-3 rounded-full bg-teal-500 hover:bg-teal-600 text-white font-semibold text-lg focus:outline-none focus:ring-4 focus:ring-teal-300"
      >
        Add Student
      </button>
    </div>
  </div>
);

export default AddStudentForm;
