import React, { useState } from 'react';

const ManageStudents = () => {
  const [students, setStudents] = useState([
    { name: 'John Doe', class: '5', studentId: '1001', vaccinationName: 'Polio', vaccinationStatus: 'Not Vaccinated' },
    { name: 'Jane Smith', class: '6', studentId: '1002', vaccinationName: 'Hepatitis', vaccinationStatus: 'Vaccinated' },
    { name: 'Alice Johnson', class: '5', studentId: '1003', vaccinationName: 'BCG', vaccinationStatus: 'Not Vaccinated' },
    { name: 'Bob Brown', class: '7', studentId: '1004', vaccinationName: 'DPT', vaccinationStatus: 'Vaccinated' },
  ]);

  const [newStudent, setNewStudent] = useState({ name: '', class: '', studentId: '', vaccinationStatus: '', vaccinationName: '' });
  const [editingIndex, setEditingIndex] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  const handleAddStudent = () => {
    if (newStudent.name && newStudent.class && newStudent.studentId && newStudent.vaccinationStatus && newStudent.vaccinationName) {
      setStudents([...students, newStudent]);
      setNewStudent({ name: '', class: '', studentId: '', vaccinationStatus: '', vaccinationName: '' });
    }
  };

  const handleDelete = (index) => {
    const updated = [...students];
    updated.splice(index, 1);
    setStudents(updated);
  };

  const handleEditClick = (index) => {
    setEditingIndex(index);
    setNewStudent({ ...students[index] });
  };

  const handleSave = () => {
    const updated = [...students];
    updated[editingIndex] = newStudent;
    setStudents(updated);
    setEditingIndex(null);
    setNewStudent({ name: '', class: '', studentId: '', vaccinationStatus: '', vaccinationName: '' });
  };

  const handleStatusChange = () => {
    if (newStudent.vaccinationStatus !== 'Vaccinated') {
      setNewStudent(prev => ({ ...prev, vaccinationStatus: 'Vaccinated' }));
    }
  };

  const filteredStudents = students.filter(s =>
    s.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    s.studentId.includes(searchTerm) ||
    s.vaccinationName.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="p-8 max-w-5xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Add/Manage Student Details</h1>

      {/* Add Student Form */}
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Add Student</h2>
        <div className="grid grid-cols-2 gap-4">
          <input
            type="text"
            placeholder="Name"
            className="border p-2 rounded"
            value={newStudent.name}
            onChange={e => setNewStudent({ ...newStudent, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Class"
            className="border p-2 rounded"
            value={newStudent.class}
            onChange={e => setNewStudent({ ...newStudent, class: e.target.value })}
          />
          <input
            type="text"
            placeholder="Student ID"
            className="border p-2 rounded"
            value={newStudent.studentId}
            onChange={e => setNewStudent({ ...newStudent, studentId: e.target.value })}
          />
          <input
            type="text"
            placeholder="Vaccination Name"
            className="border p-2 rounded"
            value={newStudent.vaccinationName}
            onChange={e => setNewStudent({ ...newStudent, vaccinationName: e.target.value })}
          />
          <select
            className="border p-2 rounded"
            value={newStudent.vaccinationStatus}
            onChange={e => setNewStudent({ ...newStudent, vaccinationStatus: e.target.value })}
          >
            <option value="">Select Status</option>
            <option value="Vaccinated">Vaccinated</option>
            <option value="Not Vaccinated">Not Vaccinated</option>
          </select>
        </div>
        {editingIndex === null ? (
          <button className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded mt-4" onClick={handleAddStudent}>
            Add Student
          </button>
        ) : (
          <div className="flex gap-4 mt-4">
            <button onClick={handleSave} className="bg-emerald-600 hover:bg-emerald-700 text-white px-2 py-1 rounded">Save</button>
            <button onClick={() => setEditingIndex(null)} className="bg-slate-500 hover:bg-slate-600 text-white px-2 py-1 rounded">Cancel</button>
            {newStudent.vaccinationStatus !== 'Vaccinated' && (
              <button onClick={handleStatusChange} className="bg-teal-600 hover:bg-teal-700 text-white px-2 py-1 rounded">Mark as Vaccinated</button>
            )}
          </div>
        )}
      </div>

      {/* Search Bar */}
      <div className="mb-6">
        <input
          type="text"
          placeholder="Search by name, ID or vaccination"
          className="border p-2 rounded w-full"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Student List */}
      <div>
        <h2 className="text-xl font-semibold mb-4">Student List</h2>
        <table className="w-full border-collapse border">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2 text-left">Name</th>
              <th className="border p-2 text-left">Class</th>
              <th className="border p-2 text-left">Student ID</th>
              <th className="border p-2 text-left">Vaccination Name</th>
              <th className="border p-2 text-left">Vaccination Status</th>
              <th className="border p-2 text-left">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredStudents.map((student, index) => (
              <tr key={index}>
                <td className="border p-2">{student.name}</td>
                <td className="border p-2">{student.class}</td>
                <td className="border p-2">{student.studentId}</td>
                <td className="border p-2">{student.vaccinationName}</td>
                <td className="border p-2">{student.vaccinationStatus}</td>
                <td className="border p-2 flex gap-2">
                  <button
                    onClick={() => handleEditClick(index)}
                    className="bg-yellow-500 hover:bg-yellow-600 text-white px-2 py-1 rounded"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(index)}
                    className="bg-rose-600 hover:bg-rose-700 text-white px-2 py-1 rounded"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManageStudents;
