import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ManageStudents = () => {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({
    name: '',
    class: '',
    studentId: '',
    vaccinationName: '',
    vaccinationStatus: 'Not Vaccinated',
    vaccinationDate: '',
  });
  const [editingIndex, setEditingIndex] = useState(null);
  const [editData, setEditData] = useState({});
  const [searchTerm, setSearchTerm] = useState('');

  const [csvFile, setCsvFile] = useState(null);
  const [csvData, setCsvData] = useState([]);

  // Fetch all students from the backend
  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/students');
      setStudents(response.data);
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  // Handle adding a new student
  const handleAddStudent = async () => {
    const { name, class: studentClass, studentId, vaccinationName, vaccinationStatus, vaccinationDate } = newStudent;

    if (!name || !studentClass || !studentId || !vaccinationName) {
      alert('Please fill all fields!');
      return;
    }

    if (vaccinationStatus === 'Vaccinated' && !vaccinationDate) {
      alert('Please provide a vaccination date!');
      return;
    }

    const payload = { ...newStudent };
    if (vaccinationStatus === 'Not Vaccinated') {
      payload.vaccinationDate = null;
    }

    try {
      await axios.post('http://localhost:5000/students', payload);
      fetchStudents();
      setNewStudent({
        name: '',
        class: '',
        studentId: '',
        vaccinationName: '',
        vaccinationStatus: 'Not Vaccinated',
        vaccinationDate: '',
      });
    } catch (err) {
      console.error('Error adding student:', err);
    }
  };

  // Handle editing a student
  const handleEditClick = (index) => {
    setEditingIndex(index);
    setEditData(students[index]);
  };

  // Handle saving edited student
  const handleSave = async () => {
    if (
      editData.vaccinationStatus === 'Vaccinated' &&
      (!editData.vaccinationDate || editData.vaccinationDate === '')
    ) {
      alert('Please provide a vaccination date!');
      return;
    }

    if (editData.vaccinationStatus === 'Not Vaccinated') {
      editData.vaccinationDate = null;
    }

    try {
      await axios.put(`http://localhost:5000/students/${editData.id}`, editData);
      fetchStudents();
      setEditingIndex(null);
      setEditData({});
    } catch (err) {
      console.error('Error updating student:', err);
    }
  };

  // Handle deleting a student
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/students/${id}`);
      fetchStudents();
    } catch (err) {
      console.error('Error deleting student:', err);
    }
  };

  // Handle marking a student as vaccinated
  const handleStatusChange = () => {
    setEditData({
      ...editData,
      vaccinationStatus: 'Vaccinated',
      vaccinationDate: new Date().toISOString().split('T')[0],
    });
  };

  // Handle file input change for CSV
  const handleCsvChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setCsvFile(file);

      const reader = new FileReader();
      reader.onload = (event) => {
        const text = event.target.result;
        const lines = text.split('\n').filter((line) => line.trim() !== '');
        const result = lines.map((line) => {
          const [name, studentClass, studentId, vaccinationName, vaccinationStatus, vaccinationDate] = line
            .split(',')
            .map((field) => field.trim());
          return {
            name,
            class: studentClass,
            studentId,
            vaccinationName,
            vaccinationStatus,
            vaccinationDate: vaccinationStatus === 'Vaccinated' ? vaccinationDate : null,
          };
        });
        setCsvData(result);
      };
      reader.readAsText(file);
    }
  };

  const handleCsvUpload = async () => {
    try {
      const response = await axios.post('http://localhost:5000/students/bulk', { students: csvData });

      if (response.status === 200) {
        // Refresh the student data from the server
        fetchStudents();

        // Clear the file and CSV data state
        setCsvFile(null);
        setCsvData([]);

        // Show success alert
        alert('Students uploaded successfully!');

        // Refresh the page to see the updated data
        window.location.reload();
      } else {
        // If response is not 200, alert failure
        alert('Failed to upload students!');
      }
    } catch (err) {
      console.error('Error uploading students:', err);
      // In case of error, show failure alert
      alert('Failed to upload students!');
    }
  };

  // Filter students based on search term
  const filteredStudents = students.filter((student) => {
    const term = searchTerm.toLowerCase();
    return (
      student.name.toLowerCase().includes(term) ||
      student.studentId.toLowerCase().includes(term) ||
      student.vaccinationName.toLowerCase().includes(term)
    );
  });

  return (
    <div className="p-8 max-w-5xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Manage Students</h1>

      {/* Add New Student */}
      <div className="mb-8 border p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Add New Student</h2>
        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
          <input
            placeholder="Name"
            value={newStudent.name}
            onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })}
            className="border p-2 rounded"
          />
          <input
            placeholder="Class"
            value={newStudent.class}
            onChange={(e) => setNewStudent({ ...newStudent, class: e.target.value })}
            className="border p-2 rounded"
          />
          <input
            placeholder="Student ID"
            value={newStudent.studentId}
            onChange={(e) => setNewStudent({ ...newStudent, studentId: e.target.value })}
            className="border p-2 rounded"
          />
          <input
            placeholder="Vaccination Name"
            value={newStudent.vaccinationName}
            onChange={(e) => setNewStudent({ ...newStudent, vaccinationName: e.target.value })}
            className="border p-2 rounded"
          />
          <select
            className="border p-2 rounded"
            value={newStudent.vaccinationStatus}
            onChange={(e) => setNewStudent({ ...newStudent, vaccinationStatus: e.target.value })}
          >
            <option value="Not Vaccinated">Not Vaccinated</option>
            <option value="Vaccinated">Vaccinated</option>
          </select>
          {newStudent.vaccinationStatus === 'Vaccinated' && (
            <input
              type="date"
              value={newStudent.vaccinationDate}
              onChange={(e) => setNewStudent({ ...newStudent, vaccinationDate: e.target.value })}
              className="border p-2 rounded"
            />
          )}
        </div>
        <button onClick={handleAddStudent} className="btn btn-sm btn-primary mt-4">
          Add Student
        </button>
      </div>

      {/* CSV Upload */}
      <div className="mb-8 border p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Bulk Import Students (CSV)</h2>
        <input
          type="file"
          accept=".csv"
          onChange={handleCsvChange}
          className="border p-2 rounded w-full mb-2"
        />
        {csvData.length > 0 && (
          <button
            onClick={handleCsvUpload}
            className="btn btn-sm btn-primary mt-2"
          >
            Upload CSV
          </button>
        )}
      </div>

      {/* Search */}
      <input
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="border p-2 rounded w-full mb-4"
      />

      {/* Table */}
      <table className="w-full border-collapse border">
        <thead>
          <tr className="bg-gray-100">
            <th className="border p-2 text-left">Name</th>
            <th className="border p-2 text-left">Class</th>
            <th className="border p-2 text-left">Student ID</th>
            <th className="border p-2 text-left">Vaccination</th>
            <th className="border p-2 text-left">Status</th>
            <th className="border p-2 text-left">Date</th>
            <th className="border p-2 text-left">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredStudents.map((student, index) => (
            <tr key={student.id}>
              {editingIndex === index ? (
                <>
                  <td className="border p-2">
                    <input
                      value={editData.name}
                      onChange={(e) => setEditData({ ...editData, name: e.target.value })}
                      className="border p-1 rounded w-full"
                    />
                  </td>
                  <td className="border p-2">
                    <input
                      value={editData.class}
                      onChange={(e) => setEditData({ ...editData, class: e.target.value })}
                      className="border p-1 rounded w-full"
                    />
                  </td>
                  <td className="border p-2">
                    <input
                      value={editData.studentId}
                      onChange={(e) => setEditData({ ...editData, studentId: e.target.value })}
                      className="border p-1 rounded w-full"
                    />
                  </td>
                  <td className="border p-2">
                    <input
                      value={editData.vaccinationName}
                      onChange={(e) => setEditData({ ...editData, vaccinationName: e.target.value })}
                      className="border p-1 rounded w-full"
                    />
                  </td>
                  <td className="border p-2">
                    {editData.vaccinationStatus === 'Vaccinated' ? (
                      <span className="text-green-600 font-semibold">Vaccinated</span>
                    ) : (
                      <>
                        <span className="mr-2">{editData.vaccinationStatus}</span>
                        <button
                          onClick={handleStatusChange}
                          className="bg-yellow-500 text-white px-2 py-1 rounded"
                        >
                          Mark as Vaccinated
                        </button>
                      </>
                    )}
                  </td>
                  <td className="border p-2">
                    {editData.vaccinationStatus === 'Vaccinated' ? (
                      <input
                        type="date"
                        value={editData.vaccinationDate || ''}
                        onChange={(e) => setEditData({ ...editData, vaccinationDate: e.target.value })}
                        className="border p-1 rounded w-full"
                      />
                    ) : (
                      <span>—</span>
                    )}
                  </td>
                  <td className="border p-2 space-x-2">
                    <button onClick={handleSave} className="bg-green-600 text-white px-2 py-1 rounded">
                      Save
                    </button>
                    <button onClick={() => setEditingIndex(null)} className="bg-gray-500 text-white px-2 py-1 rounded">
                      Cancel
                    </button>
                  </td>
                </>
              ) : (
                <>
                  <td className="border p-2">{student.name}</td>
                  <td className="border p-2">{student.class}</td>
                  <td className="border p-2">{student.studentId}</td>
                  <td className="border p-2">{student.vaccinationName}</td>
                  <td className="border p-2">
                    <span
                      className={
                        student.vaccinationStatus === 'Vaccinated'
                          ? 'text-green-600 font-semibold'
                          : 'text-red-600'
                      }
                    >
                      {student.vaccinationStatus}
                    </span>
                  </td>
                  <td className="border p-2">
                    {student.vaccinationDate
                      ? new Date(student.vaccinationDate).toLocaleDateString()
                      : '—'}
                  </td>
                  <td className="border p-2 space-x-2">
                    <button onClick={() => handleEditClick(index)} className="btn btn-sm btn-warning">
                      Edit
                    </button>
                    <button onClick={() => handleDelete(student.id)} className="btn btn-sm btn-danger">
                      Delete
                    </button>
                  </td>
                </>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageStudents;
