import React, { useEffect, useState } from 'react';
import axios from 'axios';

const PendingApprovals = () => {
  const [pendingBookings, setPendingBookings] = useState([]);

  const fetchPendingBookings = async () => {
    try {
      const res = await axios.get('http://localhost:5000/students');
      const pending = res.data.filter(student => student.bookingStatus === 'Pending');
      setPendingBookings(pending);
    } catch (err) {
      console.error('Error fetching pending bookings:', err);
    }
  };

  useEffect(() => {
    fetchPendingBookings();
  }, []);

  const handleApprove = async (student) => {
    try {
      // Send all current student data with updated booking status
      await axios.put(`http://localhost:5000/vaccination/${student.id}`, {

        vaccinationName: student.vaccinationName,
        vaccinationDate: student.vaccinationDate,
        vaccinationStatus: student.vaccinationStatus,
        bookingStatus: 'Approved',
      });

      fetchPendingBookings(); // Refresh list after update
    } catch (err) {
      console.error('Error approving booking:', err);
    }
  };

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Pending Approvals</h2>

      {pendingBookings.length === 0 ? (
        <p>No pending bookings found.</p>
      ) : (
        <table className="w-full border border-collapse">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2">Name</th>
              <th className="border p-2">Class</th>
              <th className="border p-2">Student ID</th>
              <th className="border p-2">Vaccination</th>
              <th className="border p-2">Date</th>
              <th className="border p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {pendingBookings.map(student => (
              <tr key={student.id}>
                <td className="border p-2">{student.name}</td>
                <td className="border p-2">{student.class}</td>
                <td className="border p-2">{student.studentId}</td>
                <td className="border p-2">{student.vaccinationName}</td>
                <td className="border p-2">{student.vaccinationDate ? new Date(student.vaccinationDate).toLocaleDateString() : '—'}</td>
                <td className="border p-2">
                  <button
                    onClick={() => handleApprove(student)}
                    className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
                  >
                    Approve
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PendingApprovals;
