import React, { useEffect, useState } from 'react';

const ManageVaccinations = () => {
  const [form, setForm] = useState({
    vaccineName: '',
    date: '',
    doses: '',
    classes: '',
  });

  const [drives, setDrives] = useState([]);

  const fetchDrives = async () => {
    const token = localStorage.getItem('token');
    try {
      const res = await fetch('http://localhost:5000/vaccination-drives', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setDrives(data);
    } catch (err) {
      console.error('❌ Error fetching drives:', err);
    }
  };

  useEffect(() => {
    fetchDrives();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    const newDrive = {
      vaccine: form.vaccineName,
      date: form.date,
      doses: parseInt(form.doses),
      classes: form.classes,
    };

    try {
      const res = await fetch('http://localhost:5000/vaccination-drives', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newDrive),
      });

      if (res.ok) {
        await fetchDrives();
        setForm({ vaccineName: '', date: '', doses: '', classes: '' });
      } else {
        const error = await res.json();
        alert(error.message);
      }
    } catch (err) {
      console.error('❌ Error creating drive:', err);
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Vaccination Drive Management</h2>

      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title mb-3">Create Vaccination Drive</h5>
          <form onSubmit={handleCreate}>
            <div className="mb-3">
              <label className="form-label">Vaccine Name</label>
              <input
                type="text"
                className="form-control"
                name="vaccineName"
                value={form.vaccineName}
                onChange={handleChange}
                required
              />
            </div>

            <div className="mb-3">
              <label className="form-label">Date of Drive</label>
              <input
                type="date"
                className="form-control"
                name="date"
                value={form.date}
                onChange={handleChange}
                required
              />
            </div>

            <div className="mb-3">
              <label className="form-label">Number of Available Doses</label>
              <input
                type="number"
                className="form-control"
                name="doses"
                value={form.doses}
                onChange={handleChange}
                required
              />
            </div>

            <div className="mb-3">
              <label className="form-label">Applicable Classes</label>
              <input
                type="text"
                className="form-control"
                name="classes"
                value={form.classes}
                onChange={handleChange}
                required
              />
            </div>

            <button type="submit" className="btn btn-primary">Create</button>
          </form>
        </div>
      </div>

      <div className="card">
        <div className="card-body">
          <h5 className="card-title mb-3">Upcoming Vaccination Drives</h5>
          <div className="table-responsive">
            <table className="table table-bordered table-hover">
              <thead className="table-light">
                <tr>
                  <th>Vaccine</th>
                  <th>Date</th>
                  <th>Available Doses</th>
                  <th>Applicable Classes</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {drives.length > 0 ? (
                  drives.map((drive) => (
                    <tr key={drive.id}>
                      <td>{drive.vaccine}</td>
                      <td>{drive.date}</td>
                      <td>{drive.doses}</td>
                      <td>{drive.classes}</td>
                      <td>
                        <button className="btn btn-outline-secondary btn-sm" disabled>Edit</button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="5" className="text-center">No drives available.</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ManageVaccinations;
