import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AddStudentForm from './AddStudentForm';
import CsvUpload from './CsvUpload';
import StudentTable from './StudentTable';
import PendingBookings from './PendingBookings';
import ToggleSwitch from './ToggleSwitch'; // Import the toggle component

const ManageStudents = () => {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({
    name: '',
    class: '',
    studentId: '',
    vaccinationName: '',
    vaccinationStatus: 'Not Vaccinated',
    vaccinationDate: '',
    bookingStatus: ''
  });
  const [editingIndex, setEditingIndex] = useState(null);
  const [bookingIndex, setBookingIndex] = useState(null);
  const [editData, setEditData] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [csvFile, setCsvFile] = useState(null);
  const [enableChanges, setEnableChanges] = useState(false);

  const fetchStudents = async () => {
    try {
      const res = await axios.get('http://localhost:5000/students');
      setStudents(res.data);
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleAddStudent = async () => {
    const { name, class: studentClass, studentId } = newStudent;
    if (!name || !studentClass || !studentId) return alert('Please fill all fields!');
    const payload = { ...newStudent };
    if (payload.vaccinationStatus === 'Not Vaccinated') payload.vaccinationDate = null;
    try {
      await axios.post('http://localhost:5000/students', payload);
      setNewStudent({ name: '', class: '', studentId: '' });
      fetchStudents();
    } catch (err) {
      console.error('Error adding student:', err);
    }
  };

  const handleEditClick = (index) => {
    setEditingIndex(index);
    setBookingIndex(null);
    setEditData({ ...students[index] });
  };

  const handleBookClick = (index) => {
    setBookingIndex(index);
    setEditingIndex(null);
    setEditData({ ...students[index] });
  };

  const handleSave = async () => {
    const { id, bookingStatus, vaccinationStatus } = editData;
    if ((bookingStatus === 'Pending' || !bookingStatus || bookingStatus === 'Not Approved') &&
      (!editData.name || !editData.class || !editData.studentId)) {
      return alert('Please fill all fields!');
    }
    if (bookingStatus === 'Approved' && vaccinationStatus !== 'Vaccinated') {
      return alert('You can only mark as Vaccinated.');
    }
    try {
      await axios.put(`http://localhost:5000/students/${id}`, editData);
      setEditingIndex(null);
      fetchStudents();
    } catch (err) {
      console.error('Error updating student:', err);
    }
  };

  const handleMarkAsVaccinated = async () => {
    try {
      await axios.put(`http://localhost:5000/vaccinationStatus/${editData.id}`, {
        ...editData,
        vaccinationStatus: 'Vaccinated'
      });
      setEditingIndex(null);
      fetchStudents();
    } catch (err) {
      console.error('Error marking vaccinated:', err);
    }
  };

  const handleVaccinationSave = async () => {
    const { vaccinationName, vaccinationDate, id } = editData;
    if (!vaccinationName || !vaccinationDate) return alert('Please fill all vaccination fields!');
    try {
      await axios.put(`http://localhost:5000/vaccination/${id}`, {
        vaccinationName,
        vaccinationDate,
        vaccinationStatus: 'Not Vaccinated',
        bookingStatus: 'Pending'
      });
      setBookingIndex(null);
      fetchStudents();
    } catch (err) {
      console.error('Error saving vaccination info:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/students/${id}`);
      fetchStudents();
    } catch (err) {
      console.error('Error deleting student:', err);
    }
  };

  const handleCsvUpload = async () => {
    if (!csvFile) return alert('Please select a CSV file first!');
    const formData = new FormData();
    formData.append('file', csvFile);
    try {
      await axios.post('http://localhost:5000/upload-csv', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert('CSV uploaded successfully!');
      setCsvFile(null);
      fetchStudents();
    } catch (err) {
      console.error('CSV upload error:', err);
      alert('Failed to upload CSV');
    }
  };

  const filteredStudents = students.filter((student) =>
    [student.name, student.studentId, student.class, student.vaccinationName, student.vaccinationStatus, student.bookingStatus]
      .some(field => field?.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  return (
    <div className="p-8 max-w-6xl mx-auto bg-gray-50 rounded-lg shadow-lg">
  <h1 className="mb-6 text-3xl font-semibold text-primary border-b-4 border-primary pb-2 flex items-center space-x-3">
    <i className="bi bi-speedometer2 text-primary text-xl"></i>
    <span>Manage Students</span>
  </h1>

  <ToggleSwitch
    enableChanges={enableChanges}
    setEnableChanges={setEnableChanges}
  />

  {enableChanges && <PendingBookings />}

  <div className="mt-6 bg-white p-6 rounded-lg shadow-md">
    <AddStudentForm
      newStudent={newStudent}
      setNewStudent={setNewStudent}
      handleAddStudent={handleAddStudent}
    />
  </div>

  <div className="mt-6 bg-white p-6 rounded-lg shadow-md">
    <CsvUpload
      csvFile={csvFile}
      setCsvFile={setCsvFile}
      handleCsvUpload={handleCsvUpload}
    />
  </div>

  {/* Adding extra space before and after the search input */}
  <div className="mt-6 mb-6">
    <input
      placeholder="Search..."
      value={searchTerm}
      onChange={(e) => setSearchTerm(e.target.value)}
      className="border-2 border-gray-300 p-3 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-primary"
    />
  </div>

  <div className="bg-white p-6 rounded-lg shadow-md">
    <StudentTable
      students={filteredStudents}
      editingIndex={editingIndex}
      bookingIndex={bookingIndex}
      editData={editData}
      setEditData={setEditData}
      handleEditClick={handleEditClick}
      handleBookClick={handleBookClick}
      handleDelete={handleDelete}
      handleSave={handleSave}
      handleVaccinationSave={handleVaccinationSave}
      handleMarkAsVaccinated={handleMarkAsVaccinated}
    />
  </div>
</div>
  );
};

export default ManageStudents;
