import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({
    name: '',
    class: '',
    studentId: '',
  });
  const [bookingIndex, setBookingIndex] = useState(null);
  const [bookingData, setBookingData] = useState({
    id: '',
    vaccinationName: '',
    vaccinationDate: '',
  });

  const fetchStudents = async () => {
    try {
      const res = await axios.get('http://localhost:5000/students');
      setStudents(res.data);
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleAddStudent = async () => {
    const { name, class: studentClass, studentId } = newStudent;

    if (!name || !studentClass || !studentId) {
      alert('Please fill all fields!');
      return;
    }

    try {
      await axios.post('http://localhost:5000/students', {
        ...newStudent,
        vaccinationName: '',
        vaccinationStatus: 'Not Vaccinated',
        vaccinationDate: '',
        bookingStatus: null,
      });
      setNewStudent({ name: '', class: '', studentId: '' });
      fetchStudents();
    } catch (err) {
      console.error('Error adding student:', err);
    }
  };

  const handleDeleteStudent = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/students/${id}`);
      fetchStudents();
    } catch (err) {
      console.error('Error deleting student:', err);
    }
  };

  const handleBookingClick = (index) => {
    const student = students[index];
    setBookingIndex(index);
    setBookingData({
      id: student.id,
      vaccinationName: student.vaccinationName || '',
      vaccinationDate: student.vaccinationDate || '',
    });
  };

  const handleBookingSave = async () => {
    if (!bookingData.vaccinationName || !bookingData.vaccinationDate) {
      alert('Please provide vaccination name and date');
      return;
    }

    try {
      await axios.put(`http://localhost:5000/students/${bookingData.id}`, {
        vaccinationName: bookingData.vaccinationName,
        vaccinationDate: bookingData.vaccinationDate,
        vaccinationStatus: 'Vaccinated',
        bookingStatus: 'Pending',
      });

      setBookingIndex(null);
      fetchStudents();
    } catch (err) {
      console.error('Error saving booking info:', err);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Student List</h2>
      <div className="flex gap-4 mb-6">
        <input
          placeholder="Name"
          value={newStudent.name}
          onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })}
          className="border p-2 rounded"
        />
        <input
          placeholder="Class"
          value={newStudent.class}
          onChange={(e) => setNewStudent({ ...newStudent, class: e.target.value })}
          className="border p-2 rounded"
        />
        <input
          placeholder="Student ID"
          value={newStudent.studentId}
          onChange={(e) => setNewStudent({ ...newStudent, studentId: e.target.value })}
          className="border p-2 rounded"
        />
        <button onClick={handleAddStudent} className="btn btn-primary">
          Add Student
        </button>
      </div>

      <table className="w-full border">
        <thead>
          <tr className="bg-gray-100">
            <th className="border p-2">Name</th>
            <th className="border p-2">Class</th>
            <th className="border p-2">Student ID</th>
            <th className="border p-2">Vaccination Name</th>
            <th className="border p-2">Vaccination Date</th>
            <th className="border p-2">Vaccination Status</th>
            <th className="border p-2">Booking Status</th>
            <th className="border p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student, index) => (
            <tr key={student.id}>
              <td className="border p-2">{student.name}</td>
              <td className="border p-2">{student.class}</td>
              <td className="border p-2">{student.studentId}</td>
              <td className="border p-2">
                {bookingIndex === index ? (
                  <input
                    type="text"
                    value={bookingData.vaccinationName}
                    onChange={(e) =>
                      setBookingData({ ...bookingData, vaccinationName: e.target.value })
                    }
                    className="border p-1 rounded w-full"
                  />
                ) : (
                  student.vaccinationName
                )}
              </td>
              <td className="border p-2">
                {bookingIndex === index ? (
                  <input
                    type="date"
                    value={bookingData.vaccinationDate}
                    onChange={(e) =>
                      setBookingData({ ...bookingData, vaccinationDate: e.target.value })
                    }
                    className="border p-1 rounded w-full"
                  />
                ) : (
                  student.vaccinationDate
                )}
              </td>
              <td className="border p-2">{student.vaccinationStatus}</td>
              <td className="border p-2">{student.bookingStatus || 'Not Booked'}</td>
              <td className="border p-2 space-x-2">
                {bookingIndex === index ? (
                  <button onClick={handleBookingSave} className="btn btn-sm btn-success">
                    Save
                  </button>
                ) : (
                  <>
                    <button
                      onClick={() => handleBookingClick(index)}
                      className="btn btn-sm btn-success"
                      disabled={
                        student.bookingStatus === 'Pending' ||
                        student.bookingStatus === 'Completed'
                      }
                    >
                      Book Drive
                    </button>
                    <button
                      onClick={() => handleDeleteStudent(student.id)}
                      className="btn btn-sm btn-error"
                    >
                      Delete
                    </button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StudentList;
