import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [totalStudents, setTotalStudents] = useState(0);
  const [vaccinatedCount, setVaccinatedCount] = useState(0);
  const [drives, setDrives] = useState([]);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // Fetch students
      const studentsRes = await axios.get('http://localhost:5000/students');
      const students = studentsRes.data;
      setTotalStudents(students.length);
      const vaccinated = students.filter(s => s.vaccinationStatus === 'Vaccinated');
      setVaccinatedCount(vaccinated.length);

      // Fetch drives
      const token = localStorage.getItem('token');
      const drivesRes = await axios.get('http://localhost:5000/vaccination-drives', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      // Sort by date
      const sortedDrives = drivesRes.data.sort(
        (a, b) => new Date(a.date) - new Date(b.date)
      );

      setDrives(sortedDrives);
    } catch (err) {
      console.error('Error fetching dashboard data:', err);
    }
  };

  const vaccinatedPercentage =
    totalStudents === 0 ? 0 : ((vaccinatedCount / totalStudents) * 100).toFixed(2);

  return (
    <div className="container mt-4">
      <h2>ðŸ“Š Dashboard</h2>

      <div className="row mt-4">
        <div className="col-md-4 mb-3">
          <div className="card p-3 text-center bg-light">
            <h5>Total Students</h5>
            <h3>{totalStudents}</h3>
          </div>
        </div>
        <div className="col-md-4 mb-3">
          <div className="card p-3 text-center bg-light">
            <h5>Vaccinated Students</h5>
            <h3>{vaccinatedCount}</h3>
          </div>
        </div>
        <div className="col-md-4 mb-3">
          <div className="card p-3 text-center bg-light">
            <h5>Vaccination %</h5>
            <h3>{vaccinatedPercentage}%</h3>
          </div>
        </div>
      </div>

      <div className="card mt-5 p-3">
        <h4>ðŸ“… Upcoming Vaccination Drives</h4>
        <table className="table table-bordered mt-3">
          <thead>
            <tr>
              <th>Vaccine</th>
              <th>Date</th>
              <th>Doses</th>
              <th>Classes</th>
            </tr>
          </thead>
          <tbody>
            {drives.length > 0 ? (
              drives.map((drive) => (
                <tr key={drive.id}>
                  <td>{drive.vaccine}</td>
                  <td>{drive.date}</td>
                  <td>{drive.doses}</td>
                  <td>{drive.classes}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4" className="text-center">No drives scheduled</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
