import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [vaccine, setVaccine] = useState('');
  const [date, setDate] = useState('');

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const res = await axios.get('http://localhost:5000/students');
      setStudents(res.data);
    } catch (err) {
      console.error('Failed to fetch students', err);
    }
  };

  const handleBookDrive = async (id) => {
    try {
      await axios.post(`http://localhost:5000/book-drive/${id}`, {
        vaccinationName: vaccine,
        vaccinationDate: date,
      });
      alert('Drive booked successfully!');
      fetchStudents(); // refresh the list to reflect updated status
    } catch (err) {
      console.error('Booking failed', err);
      alert('Failed to book drive.');
    }
  };

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Student List</h1>

      <div className="mb-4 flex flex-col sm:flex-row items-center gap-2">
        <input
          type="text"
          placeholder="Vaccine Name"
          value={vaccine}
          onChange={(e) => setVaccine(e.target.value)}
          className="border p-2 rounded w-full sm:w-1/3"
        />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="border p-2 rounded w-full sm:w-1/3"
        />
      </div>

      <table className="w-full border">
        <thead>
          <tr className="bg-gray-100">
            <th className="p-2 border">Name</th>
            <th className="p-2 border">Class</th>
            <th className="p-2 border">Student ID</th>
            <th className="p-2 border">Vaccine</th>
            <th className="p-2 border">Date</th>
            <th className="p-2 border">Status</th>
            <th className="p-2 border">Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => {
            const isBooked = student.bookingStatus === 'Pending' || student.bookingStatus === 'Completed';
            return (
              <tr key={student.id} className="text-center">
                <td className="p-2 border">{student.name}</td>
                <td className="p-2 border">{student.class}</td>
                <td className="p-2 border">{student.studentId}</td>
                <td className="p-2 border">{student.vaccinationName || '-'}</td>
                <td className="p-2 border">{student.vaccinationDate || '-'}</td>
                <td className="p-2 border">{student.bookingStatus || 'Not Booked'}</td>
                <td className="p-2 border">
                  <button
                    onClick={() => handleBookDrive(student.id)}
                    disabled={isBooked || !vaccine || !date}
                    className={`px-3 py-1 rounded text-white ${
                      isBooked || !vaccine || !date
                        ? 'bg-gray-400 cursor-not-allowed'
                        : 'bg-blue-500 hover:bg-blue-600'
                    }`}
                  >
                    {isBooked ? 'Booked' : 'Book Drive'}
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default StudentList;
