import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { CSVLink } from 'react-csv';

const Reports = () => {
  const [students, setStudents] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [filters, setFilters] = useState({
    name: '',
    class: '',
    studentId: '',
    vaccinationName: '',
    vaccinationDate: '',
  });
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const res = await axios.get('http://localhost:5000/students'); // Adjust endpoint
        setStudents(res.data);
        setFilteredData(res.data);
      } catch (err) {
        console.error('Fetch error:', err.message);
      }
    };
    fetchStudents();
  }, []);

  const handleFilterChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  const handleGenerate = () => {
    const filtered = students.filter((s) =>
      (filters.name ? s.name?.toLowerCase().includes(filters.name.toLowerCase()) : true) &&
      (filters.class ? s.class?.toLowerCase().includes(filters.class.toLowerCase()) : true) &&
      (filters.studentId ? s.studentId?.toString().includes(filters.studentId) : true) &&
      (filters.vaccinationName ? s.vaccinationName?.toLowerCase().includes(filters.vaccinationName.toLowerCase()) : true) &&
      (filters.vaccinationDate ? s.vaccinationDate === filters.vaccinationDate : true)
    );
    setFilteredData(filtered);
    setCurrentPage(1);
  };

  const indexOfLast = currentPage * itemsPerPage;
  const indexOfFirst = indexOfLast - itemsPerPage;
  const currentData = filteredData.slice(indexOfFirst, indexOfLast);
  const totalPages = Math.ceil(filteredData.length / itemsPerPage);

  const nextPage = () => setCurrentPage(prev => Math.min(prev + 1, totalPages));
  const prevPage = () => setCurrentPage(prev => Math.max(prev - 1, 1));

  return (
    <div className="p-4">
      <h3 className="text-xl font-bold mb-4">📋 Vaccination Reports</h3>

      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-3 mb-4">
        <input name="name" value={filters.name} onChange={handleFilterChange} placeholder="Student Name" className="border p-1 rounded" />
        <input name="class" value={filters.class} onChange={handleFilterChange} placeholder="Class" className="border p-1 rounded" />
        <input name="studentId" value={filters.studentId} onChange={handleFilterChange} placeholder="Student ID" className="border p-1 rounded" />
        <input name="vaccinationName" value={filters.vaccinationName} onChange={handleFilterChange} placeholder="Vaccine Name" className="border p-1 rounded" />
        <input type="date" name="vaccinationDate" value={filters.vaccinationDate} onChange={handleFilterChange} className="border p-1 rounded" />
      </div>

      <div className="flex gap-2 mb-4">
        <button onClick={handleGenerate} className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600">
          Generate Report
        </button>
        {filteredData.length > 0 && (
          <CSVLink
            data={filteredData}
            filename="vaccination_report.csv"
            className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600"
          >
            Download CSV
          </CSVLink>
        )}
      </div>

      <table className="w-full border text-sm text-left">
        <thead className="bg-gray-200">
          <tr>
            <th className="p-2 border">Name</th>
            <th className="p-2 border">Class</th>
            <th className="p-2 border">Student ID</th>
            <th className="p-2 border">Vaccine</th>
            <th className="p-2 border">Status</th>
            <th className="p-2 border">Date</th>
          </tr>
        </thead>
        <tbody>
          {currentData.length === 0 ? (
            <tr>
              <td colSpan="6" className="p-2 border text-center">No data found</td>
            </tr>
          ) : (
            currentData.map((s, i) => (
              <tr key={i}>
                <td className="p-2 border">{s.name}</td>
                <td className="p-2 border">{s.class}</td>
                <td className="p-2 border">{s.studentId}</td>
                <td className="p-2 border">{s.vaccinationName || '-'}</td>
                <td className="p-2 border">{s.vaccinationStatus || '-'}</td>
                <td className="p-2 border">{new Date(s.vaccinationDate).toLocaleDateString() || '-'}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="mt-4 flex items-center gap-2">
          <button onClick={prevPage} disabled={currentPage === 1} className="px-2 py-1 bg-gray-300 rounded hover:bg-gray-400 disabled:opacity-50">Prev</button>
          <span>Page {currentPage} of {totalPages}</span>
          <button onClick={nextPage} disabled={currentPage === totalPages} className="px-2 py-1 bg-gray-300 rounded hover:bg-gray-400 disabled:opacity-50">Next</button>
        </div>
      )}
    </div>
  );
};

export default Reports;
