import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [totalStudents, setTotalStudents] = useState(0);
  const [vaccinatedCount, setVaccinatedCount] = useState(0);
  const [allDrives, setAllDrives] = useState([]);
  const [upcomingDrives, setUpcomingDrives] = useState([]);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        // Get students and calculate vaccination info
        const studentsRes = await axios.get('http://localhost:5000/students');
        const students = studentsRes.data;
        setTotalStudents(students.length);

        const vaccinated = students.filter(student => student.vaccinationStatus === 'Vaccinated');
        setVaccinatedCount(vaccinated.length);

        // Get all vaccination drives
        const drivesRes = await axios.get('http://localhost:5000/vaccination-drives');
        const allDrivesRaw = drivesRes.data;

        // Sort by date
        const sortedDrives = allDrivesRaw
          .map(d => ({
            ...d,
            dateObj: new Date(d.date)
          }))
          .sort((a, b) => a.dateObj - b.dateObj);

        const today = new Date();
        const upcoming = sortedDrives.filter(d => d.dateObj >= today);
        const cleanedDrives = sortedDrives.map(({ dateObj, ...rest }) => rest);
        const cleanedUpcoming = upcoming.map(({ dateObj, ...rest }) => rest);

        setAllDrives(cleanedDrives);
        setUpcomingDrives(cleanedUpcoming);

      } catch (err) {
        console.error('Error fetching dashboard data:', err.message);
      }
    };

    fetchDashboardData();
  }, []);

  const vaccinationPercentage = totalStudents > 0
    ? Math.round((vaccinatedCount / totalStudents) * 100)
    : 0;

  return (
    <div style={{ padding: '2rem' }}>
      <h1 style={{ textAlign: 'center', marginBottom: '2rem' }}>SCHOOL VACCINATION PORTAL</h1>

      <div style={{
        display: 'flex',
        marginBottom: '2rem',
        gap: '2rem',
        justifyContent: 'center',
        flexWrap: 'wrap'
      }}>
        <div style={cardStyle}>
          <h3>Total Students</h3>
          <p style={countStyle}>{totalStudents}</p>
        </div>

        <div style={cardStyle}>
          <h3>Students Vaccinated</h3>
          <p style={countStyle}>
            {vaccinatedCount} <span style={{ fontSize: '1rem' }}>({vaccinationPercentage}%)</span>
          </p>
        </div>
      </div>

      <div style={tableContainer}>
        <h2>Upcoming Vaccination Drives</h2>
        {upcomingDrives.length > 0 ? (
          <table style={tableStyle}>
            <thead>
              <tr>
                <th style={tableHeader}>Vaccine</th>
                <th style={tableHeader}>Date</th>
                <th style={tableHeader}>Classes</th>
                <th style={tableHeader}>Doses</th>
              </tr>
            </thead>
            <tbody>
              {upcomingDrives.map((drive, index) => (
                <tr key={index}>
                  <td style={tableCell}>{drive.vaccine}</td>
                  <td style={tableCell}>{drive.date}</td>
                  <td style={tableCell}>{drive.classes}</td>
                  <td style={tableCell}>{drive.doses}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No upcoming vaccination drives</p>
        )}
      </div>

      <div style={{ ...tableContainer, marginTop: '2rem' }}>
        <h2>All Vaccination Drives</h2>
        {allDrives.length > 0 ? (
          <table style={tableStyle}>
            <thead>
              <tr>
                <th style={tableHeader}>Vaccine</th>
                <th style={tableHeader}>Date</th>
                <th style={tableHeader}>Classes</th>
                <th style={tableHeader}>Doses</th>
              </tr>
            </thead>
            <tbody>
              {allDrives.map((drive, index) => (
                <tr key={index}>
                  <td style={tableCell}>{drive.vaccine}</td>
                  <td style={tableCell}>{drive.date}</td>
                  <td style={tableCell}>{drive.classes}</td>
                  <td style={tableCell}>{drive.doses}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No vaccination drives available</p>
        )}
      </div>
    </div>
  );
};

const cardStyle = {
  padding: '1.5rem',
  borderRadius: '10px',
  background: '#e0f7fa',
  boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
  textAlign: 'center',
  minWidth: '250px'
};

const countStyle = {
  fontSize: '2.5rem',
  fontWeight: 'bold',
  marginTop: '0.5rem'
};

const tableContainer = {
  background: '#f9f9f9',
  padding: '1.5rem',
  borderRadius: '10px',
  boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
  marginTop: '2rem'
};

const tableStyle = {
  width: '100%',
  borderCollapse: 'collapse',
  marginTop: '1rem'
};

const tableHeader = {
  borderBottom: '2px solid #ccc',
  padding: '12px',
  textAlign: 'left',
  backgroundColor: '#d0f0ff'
};

const tableCell = {
  padding: '10px',
  borderBottom: '1px solid #ddd'
};

export default Dashboard;
