// Dashboard.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    axios.get('/api/dashboard-data')
      .then(res => setData(res.data))
      .catch(err => console.error(err));
  }, []);

  if (!data) return <div>Loading...</div>;

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">SCHOOL VACCINATION PORTAL</h1>
      <div className="flex space-x-4">
        <div className="bg-white p-6 shadow rounded-lg w-1/3 text-center">
          <h2 className="text-gray-600">Total Students</h2>
          <p className="text-3xl font-bold">{data.totalStudents}</p>
        </div>
        <div className="bg-white p-6 shadow rounded-lg w-1/3 text-center">
          <h2 className="text-gray-600">Students Vaccinated</h2>
          <p className="text-3xl font-bold">{data.vaccinatedStudents}</p>
          <p className="text-sm text-gray-500">({data.percentage}%)</p>
        </div>
        <div className="bg-white p-6 shadow rounded-lg w-1/3 text-center">
          <h2 className="text-gray-600">Upcoming Vaccination Drives</h2>
          {data.upcomingDrive ? (
            <p className="text-md">{data.upcomingDrive.name} on {new Date(data.upcomingDrive.date).toLocaleDateString()}</p>
          ) : (
            <p className="text-gray-500">No upcoming drives</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
