import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { CSVLink } from 'react-csv';

const Reports = () => {
  const [students, setStudents] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [vaccineFilter, setVaccineFilter] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/students'); // Add token if protected
      setStudents(response.data);
      setFilteredData(response.data);
    } catch (err) {
      console.error('âŒ Fetch Error:', err.message);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleGenerate = () => {
    const filtered = students.filter(student =>
      vaccineFilter ? student.vaccinationName?.toLowerCase().includes(vaccineFilter.toLowerCase()) : true
    );
    setFilteredData(filtered);
    setCurrentPage(1);
  };

  // Pagination
  const indexOfLast = currentPage * itemsPerPage;
  const indexOfFirst = indexOfLast - itemsPerPage;
  const currentData = filteredData.slice(indexOfFirst, indexOfLast);
  const totalPages = Math.ceil(filteredData.length / itemsPerPage);

  const nextPage = () => setCurrentPage(prev => Math.min(prev + 1, totalPages));
  const prevPage = () => setCurrentPage(prev => Math.max(prev - 1, 1));

  return (
    <div className="p-4">
      <h3 className="text-xl font-semibold mb-4">ðŸ“Š Vaccination Reports</h3>

      <div className="mb-4 flex gap-2">
        <input
          type="text"
          placeholder="Filter by vaccine name"
          value={vaccineFilter}
          onChange={(e) => setVaccineFilter(e.target.value)}
          className="border px-2 py-1 rounded"
        />
        <button
          onClick={handleGenerate}
          className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
        >
          Generate Report
        </button>
        {filteredData.length > 0 && (
          <CSVLink
            data={filteredData}
            filename="vaccination_report.csv"
            className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600"
          >
            Download CSV
          </CSVLink>
        )}
      </div>

      <table className="w-full border text-sm text-left">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2 border">Name</th>
            <th className="p-2 border">Class</th>
            <th className="p-2 border">Student ID</th>
            <th className="p-2 border">Vaccine</th>
            <th className="p-2 border">Status</th>
            <th className="p-2 border">Date</th>
          </tr>
        </thead>
        <tbody>
          {currentData.map((student, index) => (
            <tr key={index}>
              <td className="p-2 border">{student.name}</td>
              <td className="p-2 border">{student.class}</td>
              <td className="p-2 border">{student.studentId}</td>
              <td className="p-2 border">{student.vaccinationName || '-'}</td>
              <td className="p-2 border">{student.vaccinationStatus || '-'}</td>
              <td className="p-2 border">{student.vaccinationDate || '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="mt-4 flex items-center gap-2">
          <button
            onClick={prevPage}
            disabled={currentPage === 1}
            className="px-2 py-1 bg-gray-300 rounded hover:bg-gray-400 disabled:opacity-50"
          >
            Prev
          </button>
          <span>Page {currentPage} of {totalPages}</span>
          <button
            onClick={nextPage}
            disabled={currentPage === totalPages}
            className="px-2 py-1 bg-gray-300 rounded hover:bg-gray-400 disabled:opacity-50"
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
};

export default Reports;
