import React from 'react';

const FilterForm = ({ filters, onChange, onGenerate, onDownload, showDownload }) => {
  return (
    <div className="bg-white p-4 rounded-2xl shadow-md mb-6 flex flex-col gap-4">
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
        <input
          name="name"
          placeholder="Name"
          value={filters.name}
          onChange={onChange}
          className="border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
        <input
          name="class"
          placeholder="Class"
          value={filters.class}
          onChange={onChange}
          className="border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
        <input
          name="studentId"
          placeholder="Student ID"
          value={filters.studentId}
          onChange={onChange}
          className="border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
        <input
          name="vaccinationName"
          placeholder="Vaccine Name"
          value={filters.vaccinationName}
          onChange={onChange}
          className="border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
        <input
          name="vaccinationDate"
          type="date"
          value={filters.vaccinationDate}
          onChange={onChange}
          className="border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
        <input
          name="vaccinationStatus"
          placeholder="Vaccination Status"
          value={filters.vaccinationStatus}
          onChange={onChange}
          className="border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
      </div>

      <div className="flex flex-wrap gap-3 mt-2">
        <button
          onClick={onGenerate}
          className="bg-blue-500 text-white px-5 py-2 rounded-lg hover:bg-blue-600 transition duration-200"
        >
          Generate Report
        </button>
        {showDownload && (
          <button
            onClick={onDownload}
            className="bg-green-500 text-white px-5 py-2 rounded-lg hover:bg-green-600 transition duration-200"
          >
            Download CSV
          </button>
        )}
      </div>
    </div>
  );
};

export default FilterForm;
