import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [totalStudents, setTotalStudents] = useState(0);
  const [vaccinatedCount, setVaccinatedCount] = useState(0);
  const [percentage, setPercentage] = useState(0);
  const [upcomingDrive, setUpcomingDrive] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const studentsRes = await axios.get('http://localhost:5000/students');
        const students = studentsRes.data;

        const total = students.length;
        const vaccinated = students.filter(s => s.vaccinationStatus.toLowerCase() === 'vaccinated').length;
        const percent = total > 0 ? Math.round((vaccinated / total) * 100) : 0;

        setTotalStudents(total);
        setVaccinatedCount(vaccinated);
        setPercentage(percent);

        const drivesRes = await axios.get('http://localhost:5000/vaccination-drives');
        const upcoming = drivesRes.data.find(d => new Date(d.date) >= new Date());
        setUpcomingDrive(upcoming);
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="p-8 min-h-screen bg-gray-100 font-sans">
      <h1 className="text-3xl font-bold text-center mb-8 text-blue-700">School Vaccination Portal</h1>

      <div className="grid grid-cols-1 sm:grid-cols-3 gap-6">
        <div className="bg-white p-6 shadow-md rounded-xl text-center">
          <h2 className="text-lg text-gray-500">Total Students</h2>
          <p className="text-4xl font-bold text-blue-600">{totalStudents}</p>
        </div>

        <div className="bg-white p-6 shadow-md rounded-xl text-center">
          <h2 className="text-lg text-gray-500">Vaccinated Students</h2>
          <p className="text-4xl font-bold text-green-600">{vaccinatedCount}</p>
          <p className="text-sm text-gray-400 mt-1">({percentage}%)</p>
        </div>

        <div className="bg-white p-6 shadow-md rounded-xl text-center">
          <h2 className="text-lg text-gray-500">Upcoming Vaccination Drive</h2>
          {upcomingDrive ? (
            <>
              <p className="text-xl font-semibold text-purple-600">{upcomingDrive.vaccine}</p>
              <p className="text-sm text-gray-500">
                On {new Date(upcomingDrive.date).toLocaleDateString()}
              </p>
            </>
          ) : (
            <p className="text-gray-400 text-sm">No upcoming drives</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
