import React, { useState } from 'react';
import { Button, Form, Table, Card } from 'react-bootstrap';

const ManageVaccinations = () => {
  const [form, setForm] = useState({
    vaccineName: '',
    date: '',
    doses: '',
    classes: '',
  });

  const [drives, setDrives] = useState([
    { id: 1, vaccine: 'Hepatitis B', date: '2024-05-20', doses: 120, classes: 'Grades 6–8' },
    { id: 2, vaccine: 'MMR', date: '2024-06-10', doses: 150, classes: 'Grades 5–9' },
  ]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleCreate = (e) => {
    e.preventDefault();
    const newDrive = {
      id: Date.now(),
      vaccine: form.vaccineName,
      date: form.date,
      doses: form.doses,
      classes: form.classes,
    };
    setDrives([...drives, newDrive]);
    setForm({ vaccineName: '', date: '', doses: '', classes: '' });
  };

  return (
    <div>
      <h2 className="mb-4">Vaccination Drive Management</h2>

      {/* Create Form */}
      <Card className="mb-4">
        <Card.Body>
          <Card.Title>Create Vaccination Drive</Card.Title>
          <Form onSubmit={handleCreate}>
            <Form.Group className="mb-3">
              <Form.Label>Vaccine Name</Form.Label>
              <Form.Control
                type="text"
                name="vaccineName"
                value={form.vaccineName}
                onChange={handleChange}
                placeholder="Enter vaccine name"
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Date of Drive</Form.Label>
              <Form.Control
                type="date"
                name="date"
                value={form.date}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Number of Available Doses</Form.Label>
              <Form.Control
                type="number"
                name="doses"
                value={form.doses}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Applicable Classes</Form.Label>
              <Form.Control
                type="text"
                name="classes"
                value={form.classes}
                onChange={handleChange}
                placeholder="e.g., Grades 6–8"
                required
              />
            </Form.Group>

            <Button type="submit" variant="primary">Create</Button>
          </Form>
        </Card.Body>
      </Card>

      {/* Drive Table */}
      <Card>
        <Card.Body>
          <Card.Title>Upcoming Vaccination Drives</Card.Title>
          <Table striped bordered hover responsive className="mt-3">
            <thead>
              <tr>
                <th>Vaccine</th>
                <th>Date</th>
                <th>Available Doses</th>
                <th>Applicable Classes</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {drives.map((drive) => (
                <tr key={drive.id}>
                  <td>{drive.vaccine}</td>
                  <td>{drive.date}</td>
                  <td>{drive.doses}</td>
                  <td>{drive.classes}</td>
                  <td>
                    <Button size="sm" variant="outline-secondary">Edit</Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Card.Body>
      </Card>
    </div>
  );
};

export default ManageVaccinations;
