import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Section Components
const Dashboard = () => <div><h3>Dashboard</h3><p>Welcome to the dashboard.</p></div>;
const ManageStudents = () => <div><h3>Manage Students</h3><p>Student management section.</p></div>;
const ManageVaccinations = () => <div><h3>Manage Vaccinations</h3><p>Vaccination records section.</p></div>;
const Reports = () => <div><h3>Reports</h3><p>Reports and analytics section.</p></div>;

const Home = () => {
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [activeSection, setActiveSection] = useState('dashboard');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');

      if (!token) {
        navigate('/login');
        return;
      }

      try {
        const res = await fetch('http://localhost:5000/home', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (res.ok) {
          const data = await res.json();
          setMessage(data.message);
        } else {
          throw new Error('Unauthorized');
        }
      } catch (err) {
        alert('Session expired. Please login again.');
        localStorage.removeItem('token');
        navigate('/login');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const renderSection = () => {
    switch (activeSection) {
      case 'dashboard':
        return <Dashboard />;
      case 'students':
        return <ManageStudents />;
      case 'vaccinations':
        return <ManageVaccinations />;
      case 'reports':
        return <Reports />;
      default:
        return <Dashboard />;
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      {/* Sidebar */}
      <div style={{ width: '200px', background: '#f0f0f0', padding: '20px' }}>
        <h2>Menu</h2>
        <button onClick={() => setActiveSection('dashboard')}>Dashboard</button><br /><br />
        <button onClick={() => setActiveSection('students')}>Manage Students</button><br /><br />
        <button onClick={() => setActiveSection('vaccinations')}>Manage Vaccinations</button><br /><br />
        <button onClick={() => setActiveSection('reports')}>Reports</button><br /><br />
        <button onClick={handleLogout}>Logout</button>
      </div>

      {/* Content Area */}
      <div style={{ flex: 1, padding: '20px' }}>
        <h2>{message}</h2>
        {renderSection()}
      </div>
    </div>
  );
};

export default Home;
