/*
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ManageStudents = () => {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({
    name: '',
    class: '',
    studentId: '',
    vaccinationName: '',
    vaccinationStatus: 'Not Vaccinated',
    vaccinationDate: '',
    bookingStatus: '',
  });
  const [editingIndex, setEditingIndex] = useState(null);
  const [bookingIndex, setBookingIndex] = useState(null);
  const [editData, setEditData] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [csvFile, setCsvFile] = useState(null);

  const fetchStudents = async () => {
    try {
      const res = await axios.get('http://localhost:5000/students');
      setStudents(res.data);
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleAddStudent = async () => {
    const { name, class: studentClass, studentId } = newStudent;

    if (!name || !studentClass || !studentId) {
      alert('Please fill all fields!');
      return;
    }

    const payload = { ...newStudent };
    if (newStudent.vaccinationStatus === 'Not Vaccinated') {
      payload.vaccinationDate = null;
    }

    try {
      await axios.post('http://localhost:5000/students', payload);
      setNewStudent({
        name: '',
        class: '',
        studentId: '',
      });
      fetchStudents();
    } catch (err) {
      console.error('Error adding student:', err);
    }
  };

  const handleEditClick = (index) => {
    setEditingIndex(index);
    setBookingIndex(null);
    setEditData({ ...students[index] });
  };

  const handleBookClick = (index) => {
    setBookingIndex(index);
    setEditingIndex(null);
    setEditData({ ...students[index] });
  };

  const handleSave = async () => {
    const { id, bookingStatus, vaccinationStatus } = editData;

    if (bookingStatus === 'Pending' || !bookingStatus || bookingStatus === 'Not Approved') {
      const { name, class: studentClass, studentId } = editData;
      if (!name || !studentClass || !studentId) {
        alert('Please fill all fields!');
        return;
      }
    }

    if (bookingStatus === 'Approved' && vaccinationStatus !== 'Vaccinated') {
      alert('You can only mark as Vaccinated.');
      return;
    }

    try {
      await axios.put(`http://localhost:5000/students/${id}`, editData);
      setEditingIndex(null);
      fetchStudents();
    } catch (err) {
      console.error('Error updating student:', err);
    }
  };

  const handleMarkAsVaccinated = async () => {
  try {
    await axios.put(`http://localhost:5000/vaccinationStatus/${editData.id}`, {
      ...editData,
      vaccinationStatus: 'Vaccinated',
    });
    setEditingIndex(null);
    fetchStudents(); // Refreshes updated data
  } catch (err) {
    console.error('Error marking vaccinated:', err);
  }
};

  const handleVaccinationSave = async () => {
    const { vaccinationName, vaccinationDate, id } = editData;

    if (!vaccinationName || !vaccinationDate) {
      alert('Please fill all vaccination fields!');
      return;
    }

    try {
      await axios.put(`http://localhost:5000/vaccination/${id}`, {
        vaccinationName,
        vaccinationDate,
        vaccinationStatus: 'Not Vaccinated',
        bookingStatus: 'Pending',
      });

      setBookingIndex(null);
      fetchStudents();
    } catch (err) {
      console.error('Error saving vaccination info:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/students/${id}`);
      fetchStudents();
    } catch (err) {
      console.error('Error deleting student:', err);
    }
  };

  const handleCsvUpload = async () => {
    if (!csvFile) {
      alert('Please select a CSV file first!');
      return;
    }

    const formData = new FormData();
    formData.append('file', csvFile);

    try {
      await axios.post('http://localhost:5000/upload-csv', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert('CSV uploaded successfully!');
      setCsvFile(null);
      fetchStudents();
    } catch (err) {
      console.error('CSV upload error:', err);
      alert('Failed to upload CSV');
    }
  };

  const filteredStudents = students.filter((student) => {
    const term = searchTerm.toLowerCase();
    return (
      student.name?.toLowerCase().includes(term) ||
      student.studentId?.toLowerCase().includes(term) ||
      student.class?.toLowerCase().includes(term) ||
      student.vaccinationName?.toLowerCase().includes(term) ||
      student.vaccinationStatus?.toLowerCase().includes(term) ||
      student.bookingStatus?.toLowerCase().includes(term)
    );
  });

  return (
    <div className="p-8 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Manage Students</h1>


      <div className="mb-8 border p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Add New Student</h2>
        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
          <input placeholder="Name" value={newStudent.name} onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })} className="border p-2 rounded" />
          <input placeholder="Class" value={newStudent.class} onChange={(e) => setNewStudent({ ...newStudent, class: e.target.value })} className="border p-2 rounded" />
          <input placeholder="Student ID" value={newStudent.studentId} onChange={(e) => setNewStudent({ ...newStudent, studentId: e.target.value })} className="border p-2 rounded" />
        </div>
        <button onClick={handleAddStudent} className="btn btn-primary mt-4">Add Student</button>
      </div>


      <div className="mb-8 border p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Bulk Upload via CSV</h2>
        <input type="file" accept=".csv" onChange={(e) => setCsvFile(e.target.files[0])} className="mb-2" />
        <button onClick={handleCsvUpload} className="btn btn-secondary" disabled={!csvFile}>Upload CSV</button>
      </div>


      <input placeholder="Search..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} className="border p-2 rounded w-full mb-4" />


      <table className="w-full border-collapse border">
        <thead>
          <tr className="bg-gray-100">
            <th className="border p-2">Name</th>
            <th className="border p-2">Class</th>
            <th className="border p-2">Student ID</th>
            <th className="border p-2">Booking Status</th>
            <th className="border p-2">Vaccination</th>
            <th className="border p-2">Date</th>
            <th className="border p-2">Status</th>
            <th className="border p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredStudents.map((student, index) => {
            const isEditing = editingIndex === index;
            const isBooking = bookingIndex === index;

            return (
              <tr key={student.id}>
                {isEditing ? (
                  <>
                    <td className="border p-2">
                      {(editData.bookingStatus === 'Pending' || !editData.bookingStatus || editData.bookingStatus === 'Not Approved') ? (
                        <input value={editData.name} onChange={(e) => setEditData({ ...editData, name: e.target.value })} className="border p-1 rounded w-full" />
                      ) : (
                        editData.name
                      )}
                    </td>
                    <td className="border p-2">
                      {(editData.bookingStatus === 'Pending' || !editData.bookingStatus || editData.bookingStatus === 'Not Approved') ? (
                        <input value={editData.class} onChange={(e) => setEditData({ ...editData, class: e.target.value })} className="border p-1 rounded w-full" />
                      ) : (
                        editData.class
                      )}
                    </td>
                    <td className="border p-2">
                      {(editData.bookingStatus === 'Pending' || !editData.bookingStatus || editData.bookingStatus === 'Not Approved') ? (
                        <input value={editData.studentId} onChange={(e) => setEditData({ ...editData, studentId: e.target.value })} className="border p-1 rounded w-full" />
                      ) : (
                        editData.studentId
                      )}
                    </td>
                    <td className="border p-2">{editData.bookingStatus}</td>
                    <td className="border p-2">{editData.vaccinationName}</td>
                    <td className="border p-2">{editData.vaccinationDate ? new Date(editData.vaccinationDate).toLocaleDateString() : '—'}</td>
                    <td className="border p-2">
                      {editData.bookingStatus === 'Approved' && editData.vaccinationStatus !== 'Vaccinated' ? (
                        <button onClick={handleMarkAsVaccinated} className="bg-blue-600 text-white px-2 py-1 rounded">Mark as Vaccinated</button>
                      ) : (
                        editData.vaccinationStatus
                      )}
                    </td>
                    <td className="border p-2 space-x-2">
                      <button onClick={handleSave} className="bg-green-600 text-white px-2 py-1 rounded">Save</button>
                      <button onClick={() => setEditingIndex(null)} className="bg-gray-500 text-white px-2 py-1 rounded">Cancel</button>
                    </td>
                  </>
                ) : isBooking ? (
                  <>
                    <td className="border p-2">{student.name}</td>
                    <td className="border p-2">{student.class}</td>
                    <td className="border p-2">{student.studentId}</td>
