import { useEffect, useState } from 'react';
import StudentTable from './/StudentTable';

const Dashboard = () => {
  const [students, setStudents] = useState([]);
  const [filteredStudents, setFilteredStudents] = useState([]);
  const [editingIndex, setEditingIndex] = useState(null);
  const [bookingIndex, setBookingIndex] = useState(null);
  const [editData, setEditData] = useState({});

  useEffect(() => {
    const mockStudents = [
      { id: 1, name: 'Alice', class: '10A', studentId: 'ST001', bookingStatus: 'Pending' },
      { id: 2, name: 'Bob', class: '10B', studentId: 'ST002', bookingStatus: 'Booked', vaccine: 'Covaxin', date: '2025-04-30', status: 'Vaccinated' }
    ];
    setStudents(mockStudents);
    setFilteredStudents(mockStudents);
  }, []);

  const handleEditClick = (index) => {
    setEditingIndex(index);
    setEditData({ ...filteredStudents[index] });
  };

  const handleBookClick = (index) => {
    setBookingIndex(index);
    setEditData({ vaccine: '', date: '' });
  };

  const handleSave = (index) => {
    const updated = [...students];
    updated[index] = { ...updated[index], name: editData.name, class: editData.class };
    setStudents(updated);
    setFilteredStudents(updated);
    setEditingIndex(null);
    setEditData({});
  };

  const handleVaccinationSave = (index) => {
    const updated = [...students];
    updated[index] = {
      ...updated[index],
      bookingStatus: 'Booked',
      vaccine: editData.vaccine,
      date: editData.date,
      status: 'Pending'
    };
    setStudents(updated);
    setFilteredStudents(updated);
    setBookingIndex(null);
    setEditData({});
  };

  const handleDelete = (id) => {
    const updated = students.filter((student) => student.id !== id);
    setStudents(updated);
    setFilteredStudents(updated);
  };

  const handleMarkAsVaccinated = (id) => {
    const updated = students.map((student) =>
      student.id === id ? { ...student, status: 'Vaccinated' } : student
    );
    setStudents(updated);
    setFilteredStudents(updated);
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">School Vaccination Portal</h1>
      <StudentTable
        students={students}
        filteredStudents={filteredStudents}
        editingIndex={editingIndex}
        bookingIndex={bookingIndex}
        editData={editData}
        setEditData={setEditData}
        setEditingIndex={setEditingIndex}
        setBookingIndex={setBookingIndex}
        handleEditClick={handleEditClick}
        handleBookClick={handleBookClick}
        handleSave={handleSave}
        handleVaccinationSave={handleVaccinationSave}
        handleDelete={handleDelete}
        handleMarkAsVaccinated={handleMarkAsVaccinated}
      />
    </div>
  );
};

export default Dashboard;
