import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [totalStudents, setTotalStudents] = useState(0);
  const [vaccinatedCount, setVaccinatedCount] = useState(0);
  const [upcomingDrive, setUpcomingDrive] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        // Get all students
        const studentsRes = await axios.get('http://localhost:5000/students');
        const students = studentsRes.data;
        setTotalStudents(students.length);

        // Count vaccinated students
        const vaccinated = students.filter(student => student.vaccinationStatus === 'Vaccinated');
        setVaccinatedCount(vaccinated.length);

        // Get all vaccination drives
        const drivesRes = await axios.get('http://localhost:5000/vaccination-drives');

        // Get today's date in 'YYYY-MM-DD' format
        const today = new Date().toISOString().split('T')[0];

        // Filter future drives and sort by date
        const upcoming = drivesRes.data
          .filter(d => d.date >= today)
          .sort((a, b) => new Date(a.date) - new Date(b.date))[0];

        setUpcomingDrive(upcoming);
      } catch (err) {
        console.error('Error fetching dashboard data:', err.message);
      }
    };

    fetchDashboardData();
  }, []);

  const vaccinationPercentage = totalStudents > 0
    ? Math.round((vaccinatedCount / totalStudents) * 100)
    : 0;

  return (
    <div style={{ padding: '2rem' }}>
      <h1>SCHOOL VACCINATION PORTAL</h1>
      <div style={{
        display: 'flex',
        marginTop: '2rem',
        gap: '2rem',
        justifyContent: 'space-around',
        alignItems: 'center'
      }}>
        <div style={cardStyle}>
          <h3>Total Students</h3>
          <p style={countStyle}>{totalStudents}</p>
        </div>
        <div style={cardStyle}>
          <h3>Students Vaccinated</h3>
          <p style={countStyle}>
            {vaccinatedCount} <span style={{ fontSize: '1rem' }}>({vaccinationPercentage}%)</span>
          </p>
        </div>
        <div style={cardStyle}>
          <h3>Upcoming Vaccination Drives</h3>
          {upcomingDrive ? (
            <div>
              <p><strong>Vaccine:</strong> {upcomingDrive.vaccine}</p>
              <p><strong>Date:</strong> {upcomingDrive.date}</p>
              <p><strong>Classes:</strong> {upcomingDrive.classes}</p>
            </div>
          ) : (
            <p>No upcoming drives</p>
          )}
        </div>
      </div>
    </div>
  );
};

const cardStyle = {
  padding: '1.5rem',
  borderRadius: '10px',
  background: '#f0f4ff',
  boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
  textAlign: 'center',
  flex: 1
};

const countStyle = {
  fontSize: '2.5rem',
  fontWeight: 'bold',
  marginTop: '0.5rem'
};

export default Dashboard;
