import React, { useState } from 'react';

const ManageStudents = () => {
  const [students, setStudents] = useState([
    { name: 'John Doe', class: '5', studentId: '1001', vaccinationStatus: 'Not Vaccinated', vaccinationName: 'Polio' },
    { name: 'Jane Smith', class: '6', studentId: '1002', vaccinationStatus: 'Vaccinated', vaccinationName: 'MMR' },
  ]);

  const [newStudent, setNewStudent] = useState({
    name: '',
    class: '',
    studentId: '',
    vaccinationName: '',
    vaccinationStatus: 'Not Vaccinated',
  });

  const [editingIndex, setEditingIndex] = useState(null);
  const [editData, setEditData] = useState({});
  const [searchTerm, setSearchTerm] = useState('');

  const handleAddStudent = () => {
    if (!newStudent.name || !newStudent.class || !newStudent.studentId || !newStudent.vaccinationName) {
      alert("Please fill all fields!");
      return;
    }

    setStudents([...students, newStudent]);
    setNewStudent({
      name: '',
      class: '',
      studentId: '',
      vaccinationName: '',
      vaccinationStatus: 'Not Vaccinated',
    });
  };

  const handleEditClick = (index) => {
    setEditingIndex(index);
    setEditData(students[index]);
  };

  const handleSave = () => {
    const updated = [...students];
    updated[editingIndex] = editData;
    setStudents(updated);
    setEditingIndex(null);
    setEditData({});
  };

  const handleDelete = (index) => {
    const updated = students.filter((_, i) => i !== index);
    setStudents(updated);
  };

  const handleStatusChange = () => {
    if (editData.vaccinationStatus === 'Not Vaccinated') {
      setEditData({ ...editData, vaccinationStatus: 'Vaccinated' });
    }
  };

  const filteredStudents = students.filter((student) => {
    const term = searchTerm.toLowerCase();
    return (
      student.name.toLowerCase().includes(term) ||
      student.studentId.toLowerCase().includes(term) ||
      student.vaccinationName.toLowerCase().includes(term)
    );
  });

  return (
    <div className="p-8 max-w-5xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Manage Students</h1>

      {/* Add Student Form */}
      <div className="mb-8 border p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Add New Student</h2>
        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
          <input
            type="text"
            placeholder="Name"
            className="border p-2 rounded"
            value={newStudent.name}
            onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Class"
            className="border p-2 rounded"
            value={newStudent.class}
            onChange={(e) => setNewStudent({ ...newStudent, class: e.target.value })}
          />
          <input
            type="text"
            placeholder="Student ID"
            className="border p-2 rounded"
            value={newStudent.studentId}
            onChange={(e) => setNewStudent({ ...newStudent, studentId: e.target.value })}
          />
          <input
            type="text"
            placeholder="Vaccination Name"
            className="border p-2 rounded"
            value={newStudent.vaccinationName}
            onChange={(e) => setNewStudent({ ...newStudent, vaccinationName: e.target.value })}
          />
          <select
            className="border p-2 rounded"
            value={newStudent.vaccinationStatus}
            onChange={(e) => setNewStudent({ ...newStudent, vaccinationStatus: e.target.value })}
          >
            <option value="Not Vaccinated">Not Vaccinated</option>
            <option value="Vaccinated">Vaccinated</option>
          </select>
        </div>
        <button
          className="bg-blue-600 text-white px-4 py-2 rounded mt-4"
          onClick={handleAddStudent}
        >
          Add Student
        </button>
      </div>

      {/* Search */}
      <input
        type="text"
        placeholder="Search by name, student ID, or vaccination name"
        className="border p-2 rounded w-full mb-4"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Student Table */}
      <table className="w-full border-collapse border">
        <thead>
          <tr className="bg-gray-100">
            <th className="border p-2 text-left">Name</th>
            <th className="border p-2 text-left">Class</th>
            <th className="border p-2 text-left">Student ID</th>
            <th className="border p-2 text-left">Vaccination Name</th>
            <th className="border p-2 text-left">Vaccination Status</th>
            <th className="border p-2 text-left">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredStudents.map((student, index) => (
            <tr key={index}>
              {editingIndex === index ? (
                <>
                  <td className="border p-2">
                    <input
                      value={editData.name}
                      onChange={(e) => setEditData({ ...editData, name: e.target.value })}
                      className="border p-1 rounded w-full"
                    />
                  </td>
                  <td className="border p-2">
                    <input
                      value={editData.class}
                      onChange={(e) => setEditData({ ...editData, class: e.target.value })}
                      className="border p-1 rounded w-full"
                    />
                  </td>
                  <td className="border p-2">
                    <input
                      value={editData.studentId}
                      onChange={(e) => setEditData({ ...editData, studentId: e.target.value })}
                      className="border p-1 rounded w-full"
                    />
                  </td>
                  <td className="border p-2">
                    <input
                      value={editData.vaccinationName}
                      onChange={(e) => setEditData({ ...editData, vaccinationName: e.target.value })}
                      className="border p-1 rounded w-full"
                    />
                  </td>
                  <td className="border p-2">
                    {editData.vaccinationStatus === 'Vaccinated' ? (
                      <span className="text-green-600 font-semibold">Vaccinated (Locked)</span>
                    ) : (
                      <>
                        <span className="mr-2">{editData.vaccinationStatus}</span>
                        <button
                          onClick={handleStatusChange}
                          className="bg-yellow-500 text-white px-2 py-1 rounded"
                        >
                          Mark as Vaccinated
                        </button>
                      </>
                    )}
                  </td>
                  <td className="border p-2 space-x-2">
                    <button
                      onClick={handleSave}
                      className="bg-green-600 text-white px-2 py-1 rounded"
                    >
                      Save
                    </button>
                    <button
                      onClick={() => setEditingIndex(null)}
                      className="bg-gray-500 text-white px-2 py-1 rounded"
                    >
                      Cancel
                    </button>
                  </td>
                </>
              ) : (
                <>
                  <td className="border p-2">{student.name}</td>
                  <td className="border p-2">{student.class}</td>
                  <td className="border p-2">{student.studentId}</td>
                  <td className="border p-2">{student.vaccinationName}</td>
                  <td className="border p-2">
                    <span className={student.vaccinationStatus === 'Vaccinated' ? 'text-green-600 font-semibold' : 'text-red-600'}>
                      {student.vaccinationStatus}
                    </span>
                  </td>
                  <td className="border p-2 space-x-2">
                    <button
                      onClick={() => handleEditClick(index)}
                      className="bg-blue-600 text-white px-2 py-1 rounded"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(index)}
                      className="bg-red-600 text-white px-2 py-1 rounded"
                    >
                      Delete
                    </button>
                  </td>
                </>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageStudents;
