const DriveNotices = ({ drives, onView }) => {
  return (
    <div className="mt-5">
      <h4 className="mb-4">ðŸ“¢ Upcoming Vaccination Drives</h4>
      {drives.length > 0 ? (
        <div className="d-flex flex-column gap-3">
          {drives.map((drive) => (
            <div key={drive.id} className="card shadow-sm border-left-primary p-3">
              <h5 className="mb-2">{drive.vaccine}</h5>
              <p className="mb-2">
                <strong>Date:</strong> {new Date(drive.date).toLocaleDateString()}
              </p>
              <button
                className="btn btn-outline-primary btn-sm align-self-start"
                onClick={() => onView(drive)}
              >
                View More
              </button>
            </div>
          ))}
        </div>
      ) : (
        <div className="alert alert-info text-center">
          No drives scheduled at the moment. Stay tuned!
        </div>
      )}
    </div>
  );
};

export default DriveNotices;
