import React, { useEffect, useState } from "react";
import axios from "axios";

const StudentPage = () => {
  const [students, setStudents] = useState([]);
  const [name, setName] = useState("");
  const [className, setClassName] = useState("");
  const [studentId, setStudentId] = useState("");
  const [editingIndex, setEditingIndex] = useState(null);
  const [bookingIndex, setBookingIndex] = useState(null);
  const [editData, setEditData] = useState({});
  const [searchTerm, setSearchTerm] = useState("");
  const [showPending, setShowPending] = useState(false);

  const filteredStudents = students.filter((student) =>
    student.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const fetchStudents = async () => {
    const res = await axios.get("http://localhost:3001/api/students");
    setStudents(res.data);
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleAddStudent = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:3001/api/students", {
      name,
      class: className,
      studentId,
    });
    setName("");
    setClassName("");
    setStudentId("");
    fetchStudents();
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:3001/api/students/${id}`);
    fetchStudents();
  };

  const handleEditClick = (index) => {
    setEditingIndex(index);
    setEditData({ ...students[index] });
  };

  const handleSave = async () => {
    await axios.put(
      `http://localhost:3001/api/students/${students[editingIndex].id}`,
      editData
    );
    setEditingIndex(null);
    fetchStudents();
  };

  const handleBookClick = (index) => {
    setBookingIndex(index);
    setEditData({ ...students[index] });
  };

  const handleVaccinationSave = async () => {
    await axios.put(
      `http://localhost:3001/api/students/${students[bookingIndex].id}`,
      {
        ...editData,
        bookingStatus: "Pending",
      }
    );
    setBookingIndex(null);
    fetchStudents();
  };

  const handleMarkAsVaccinated = async (id) => {
    await axios.put(`http://localhost:3001/api/students/${id}`, {
      vaccinationStatus: "Vaccinated",
    });
    fetchStudents();
  };

  const handleTogglePending = () => {
    setShowPending(!showPending);
  };

  return (
    <div className="p-4">
      <h1 className="text-3xl font-bold mb-4">Student Management</h1>
      <form onSubmit={handleAddStudent} className="mb-4 flex gap-2">
        <input
          type="text"
          placeholder="Name"
          className="border p-2 rounded"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Class"
          className="border p-2 rounded"
          value={className}
          onChange={(e) => setClassName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Student ID"
          className="border p-2 rounded"
          value={studentId}
          onChange={(e) => setStudentId(e.target.value)}
          required
        />
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
          Add Student
        </button>
      </form>

      <div className="mb-4 flex justify-between">
        <input
          type="text"
          placeholder="Search student..."
          className="border p-2 rounded w-1/3"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button
          onClick={handleTogglePending}
          className="bg-purple-600 text-white px-4 py-2 rounded"
        >
          {showPending ? "Show All Students" : "Show Pending Bookings"}
        </button>
      </div>

      {showPending ? (
        <div>
          <h2 className="text-xl font-semibold mb-2">Pending Bookings</h2>
          <table className="w-full border-collapse border">
            <thead>
              <tr className="bg-gray-100">
                <th className="border p-2">Name</th>
                <th className="border p-2">Class</th>
                <th className="border p-2">Vaccination</th>
                <th className="border p-2">Date</th>
                <th className="border p-2">Status</th>
              </tr>
            </thead>
            <tbody>
              {students
                .filter((s) => s.bookingStatus === "Pending")
                .map((student) => (
                  <tr key={student.id}>
                    <td className="border p-2">{student.name}</td>
                    <td className="border p-2">{student.class}</td>
                    <td className="border p-2">{student.vaccinationName}</td>
                    <td className="border p-2">
                      {student.vaccinationDate
                        ? new Date(student.vaccinationDate).toLocaleDateString()
                        : "—"}
                    </td>
                    <td className="border p-2">{student.vaccinationStatus}</td>
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      ) : (
        <table className="w-full border-collapse border mt-4">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2">Name</th>
              <th className="border p-2">Class</th>
              <th className="border p-2">Student ID</th>
              <th className="border p-2">Vaccination</th>
              <th className="border p-2">Date</th>
              <th className="border p-2">Status</th>
              <th className="border p-2">Booking</th>
              <th className="border p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredStudents.map((student, index) => (
              <tr key={student.id}>
                <td className="border p-2">
                  {editingIndex === index ? (
                    <input
                      value={editData.name}
                      onChange={(e) =>
                        setEditData({ ...editData, name: e.target.value })
                      }
                      className="border p-1 rounded w-full"
                    />
                  ) : (
                    student.name
                  )}
                </td>
                <td className="border p-2">
                  {editingIndex === index ? (
                    <input
                      value={editData.class}
                      onChange={(e) =>
                        setEditData({ ...editData, class: e.target.value })
                      }
                      className="border p-1 rounded w-full"
                    />
                  ) : (
                    student.class
                  )}
                </td>
                <td className="border p-2">
                  {editingIndex === index ? (
                    <input
                      value={editData.studentId}
                      onChange={(e) =>
                        setEditData({ ...editData, studentId: e.target.value })
                      }
                      className="border p-1 rounded w-full"
                    />
                  ) : (
                    student.studentId
                  )}
                </td>
                <td className="border p-2">
                  {bookingIndex === index ? (
                    <input
                      value={editData.vaccinationName}
                      onChange={(e) =>
                        setEditData({
                          ...editData,
                          vaccinationName: e.target.value,
                        })
                      }
                      className="border p-1 rounded w-full"
                    />
                  ) : (
                    student.vaccinationName || "—"
                  )}
                </td>
                <td className="border p-2">
                  {bookingIndex === index ? (
                    <input
                      type="date"
                      value={editData.vaccinationDate?.slice(0, 10)}
                      onChange={(e) =>
                        setEditData({
                          ...editData,
                          vaccinationDate: e.target.value,
                        })
                      }
                      className="border p-1 rounded w-full"
                    />
                  ) : student.vaccinationDate ? (
                    new Date(student.vaccinationDate).toLocaleDateString()
                  ) : (
                    "—"
                  )}
                </td>
                <td className="border p-2">{student.vaccinationStatus}</td>
                <td className="border p-2">{student.bookingStatus || "—"}</td>
                <td className="border p-2 space-x-2">
                  {editingIndex === index ? (
                    <>
                      <button
                        onClick={handleSave}
                        className="bg-blue-600 text-white px-2 py-1 rounded"
                      >
                        Save
                      </button>
                      <button
                        onClick={() => setEditingIndex(null)}
                        className="bg-gray-400 text-white px-2 py-1 rounded"
                      >
                        Cancel
                      </button>
                    </>
                  ) : bookingIndex === index ? (
                    <>
                      <button
                        onClick={handleVaccinationSave}
                        className="bg-green-600 text-white px-2 py-1 rounded"
                      >
                        Book
                      </button>
                      <button
                        onClick={() => setBookingIndex(null)}
                        className="bg-gray-400 text-white px-2 py-1 rounded"
                      >
                        Cancel
                      </button>
                    </>
                  ) : (
                    <>
                      <button
                        onClick={() => handleEditClick(index)}
                        className="bg-yellow-500 text-white px-2 py-1 rounded"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleBookClick(index)}
                        className="bg-purple-600 text-white px-2 py-1 rounded"
                        disabled={
                          student.bookingStatus === "Pending" ||
                          student.bookingStatus === "Approved"
                        }
                      >
                        Book Drive
                      </button>
                      <button
                        onClick={() => handleDelete(student.id)}
                        className="bg-red-600 text-white px-2 py-1 rounded"
                      >
                        Delete
                      </button>
                      {student.bookingStatus === "Approved" &&
                        student.vaccinationStatus === "Not Vaccinated" && (
                          <button
                            onClick={() =>
                              handleMarkAsVaccinated(student.id)
                            }
                            className="bg-green-700 text-white px-2 py-1 rounded"
                          >
                            Mark as Vaccinated
                          </button>
                        )}
                    </>
                  )}
                </td>
              </tr>
            ))}
            {filteredStudents.length === 0 && (
              <tr>
                <td colSpan="8" className="text-center py-4 text-gray-600">
                  No students found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default StudentPage;
