import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { CSVLink } from 'react-csv';

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({
    name: '',
    class: '',
    studentId: '',
  });
  const [bookingIndex, setBookingIndex] = useState(null);
  const [bookingData, setBookingData] = useState({
    id: '',
    vaccinationName: '',
    vaccinationDate: '',
  });
  const [editingIndex, setEditingIndex] = useState(null);
  const [editData, setEditData] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [file, setFile] = useState(null);

  const fetchStudents = async () => {
    try {
      const res = await axios.get('http://localhost:5000/students');
      setStudents(res.data);
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleAddStudent = async () => {
    const { name, class: studentClass, studentId } = newStudent;

    if (!name || !studentClass || !studentId) {
      alert('Please fill all fields!');
      return;
    }

    try {
      await axios.post('http://localhost:5000/students', {
        ...newStudent,
        vaccinationName: '',
        vaccinationStatus: 'Not Vaccinated',
        vaccinationDate: '',
        bookingStatus: null,
      });
      setNewStudent({ name: '', class: '', studentId: '' });
      fetchStudents();
    } catch (err) {
      console.error('Error adding student:', err);
    }
  };

  const handleDeleteStudent = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/students/${id}`);
      fetchStudents();
    } catch (err) {
      console.error('Error deleting student:', err);
    }
  };

  const handleBookingClick = (index) => {
    const student = students[index];
    setBookingIndex(index);
    setBookingData({
      id: student.id,
      vaccinationName: student.vaccinationName || '',
      vaccinationDate: student.vaccinationDate || '',
    });
  };

  const handleBookingSave = async () => {
    if (!bookingData.vaccinationName || !bookingData.vaccinationDate) {
      alert('Please provide vaccination name and date');
      return;
    }

    try {
      await axios.put(`http://localhost:5000/students/${bookingData.id}`, {
        vaccinationName: bookingData.vaccinationName,
        vaccinationDate: bookingData.vaccinationDate,
        vaccinationStatus: 'Vaccinated',
        bookingStatus: 'Pending',
      });

      setBookingIndex(null);
      fetchStudents();
    } catch (err) {
      console.error('Error saving booking info:', err);
    }
  };

  const handleEditClick = (index) => {
    setEditingIndex(index);
    setEditData({ ...students[index] });
  };

  const handleEditSave = async () => {
    try {
      await axios.put(`http://localhost:5000/students/${editData.id}`, {
        name: editData.name,
        class: editData.class,
        studentId: editData.studentId,
      });
      setEditingIndex(null);
      fetchStudents();
    } catch (err) {
      console.error('Error saving student edit:', err);
    }
  };

  const handleBulkUpload = async () => {
    if (!file) {
      alert('Please select a CSV file to upload');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      await axios.post('http://localhost:5000/students/bulk-upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      fetchStudents();
    } catch (err) {
      console.error('Error uploading bulk data:', err);
    }
  };

  const filteredStudents = students.filter((student) => {
    const term = searchTerm.toLowerCase();
    return (
      student.name?.toLowerCase().includes(term) ||
      student.studentId?.toLowerCase().includes(term) ||
      student.vaccinationName?.toLowerCase().includes(term)
    );
  });

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Student List</h2>

      {/* Add Student Form */}
      <div className="flex gap-4 mb-6">
        <input
          placeholder="Name"
          value={newStudent.name}
          onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })}
          className="border p-2 rounded"
        />
        <input
          placeholder="Class"
          value={newStudent.class}
          onChange={(e) => setNewStudent({ ...newStudent, class: e.target.value })}
          className="border p-2 rounded"
        />
        <input
          placeholder="Student ID"
          value={newStudent.studentId}
          onChange={(e) => setNewStudent({ ...newStudent, studentId: e.target.value })}
          className="border p-2 rounded"
        />
        <button onClick={handleAddStudent} className="btn btn-primary">
          Add Student
        </button>
      </div>

      {/* Bulk Upload Section */}
      <div className="mb-6">
        <input
          type="file"
          accept=".csv"
          onChange={(e) => setFile(e.target.files[0])}
          className="border p-2 rounded"
        />
        <button onClick={handleBulkUpload} className="btn btn-info ml-4">
          Upload CSV
        </button>
      </div>

      {/* Search Field */}
      <div className="mb-4">
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="border p-2 rounded w-full"
        />
      </div>

      {/* Export CSV */}
      <div className="mb-6">
        <CSVLink data={students} className="btn btn-success">
          Export CSV
        </CSVLink>
      </div>

      {/* Students Table */}
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-100">
            <th className="border p-2">Name</th>
            <th className="border p-2">Class</th>
            <th className="border p-2">Student ID</th>
            <th className="border p-2">Vaccination Name</th>
            <th className="border p-2">Vaccination Date</th>
            <th className="border p-2">Vaccination Status</th>
            <th className="border p-2">Booking Status</th>
            <th className="border p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredStudents.map((student, index) => (
            <tr key={student.id}>
              {/* Editable Row */}
              {editingIndex === index ? (
                <>
                  <td className="border p-2">
                    <input
                      type="text"
                      value={editData.name}
                      onChange={(e) => setEditData({ ...editData, name: e.target.value })}
                      className="border p-1 rounded w-full"
                    />
                  </td>
                  <td className="border p-2">
                    <input
                      type="text"
                      value={editData.class}
                      onChange={(e) => setEditData({ ...editData, class: e.target.value })}
                      className="border p-1 rounded w-full"
                    />
                  </td>
                  <td className="border p-2">
                    <input
                      type="text"
                      value={editData.studentId}
                      onChange={(e) => setEditData({ ...editData, studentId: e.target.value })}
                      className="border p-1 rounded w-full"
                    />
                  </td>
                  <td className="border p-2" colSpan={3}>—</td>
                  <td className="border p-2 space-x-2">
                    <button onClick={handleEditSave} className="btn btn-sm btn-success">
                      Save
                    </button>
                    <button onClick={() => setEditingIndex(null)} className="btn btn-sm btn-warning">
                      Cancel
                    </button>
                  </td>
                </>
              ) : (
                <>
                  <td className="border p-2">{student.name}</td>
                  <td className="border p-2">{student.class}</td>
                  <td className="border p-2">{student.studentId}</td>
                  <td className="border p-2">{student.vaccinationName || '—'}</td>
                  <td className="border p-2">{student.vaccinationDate || '—'}</td>
                  <td className="border p-2">{student.vaccinationStatus}</td>
                  <td className="border p-2">{student.bookingStatus || 'Not Booked'}</td>
                  <td className="border p-2 space-x-2">
                    <button onClick={() => handleEditClick(index)} className="btn btn-sm btn-warning">
                      Edit
                    </button>
                    <button onClick={() => handleDeleteStudent(student.id)} className="btn btn-sm btn-error">
                      Delete
                    </button>
                    <button
                      onClick={() => handleBookingClick(index)}
                      className="btn btn-sm btn-success"
                      disabled={student.bookingStatus === 'Pending'}
                    >
                      Book Drive
                    </button>
                  </td>
                </>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StudentList;
