import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [editingIndex, setEditingIndex] = useState(null);
  const [editData, setEditData] = useState({});
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const res = await axios.get('http://localhost:5000/students');
      setStudents(res.data);
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  };

  const handleEditClick = (index) => {
    setEditingIndex(index);
    setEditData({ ...students[index] });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    if (!editData.vaccinationName || !editData.vaccinationDate) {
      alert('Please fill vaccination name and date!');
      return;
    }

    const payload = {
      ...editData,
      vaccinationStatus: 'Vaccinated',
      bookingStatus: 'Pending',
    };

    try {
      await axios.put(`http://localhost:5000/students/${editData.id}`, payload);
      setEditingIndex(null);
      fetchStudents();
    } catch (err) {
      console.error('Error saving data:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/students/${id}`);
      fetchStudents();
    } catch (err) {
      console.error('Error deleting student:', err);
    }
  };

  const handleBookDrive = (index) => {
    setEditingIndex(index);
    setEditData({
      ...students[index],
      vaccinationName: '',
      vaccinationDate: '',
    });
  };

  const filteredStudents = students.filter((student) =>
    student.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Student List</h1>

      <input
        type="text"
        placeholder="Search by name..."
        className="border p-2 mb-4 w-full max-w-md"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <table className="table-auto border-collapse border w-full">
        <thead>
          <tr>
            <th className="border p-2">Name</th>
            <th className="border p-2">Age</th>
            <th className="border p-2">Gender</th>
            <th className="border p-2">Class</th>
            <th className="border p-2">Section</th>
            <th className="border p-2">Vaccination Name</th>
            <th className="border p-2">Vaccination Date</th>
            <th className="border p-2">Vaccination Status</th>
            <th className="border p-2">Booking Status</th>
            <th className="border p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredStudents.map((student, index) => (
            <tr key={student.id}>
              <td className="border p-2">{student.name}</td>
              <td className="border p-2">{student.age}</td>
              <td className="border p-2">{student.gender}</td>
              <td className="border p-2">{student.class}</td>
              <td className="border p-2">{student.section}</td>
              <td className="border p-2">
                {editingIndex === index ? (
                  <input
                    type="text"
                    name="vaccinationName"
                    value={editData.vaccinationName || ''}
                    onChange={handleInputChange}
                    className="border p-1 w-full"
                  />
                ) : (
                  student.vaccinationName
                )}
              </td>
              <td className="border p-2">
                {editingIndex === index ? (
                  <input
                    type="date"
                    name="vaccinationDate"
                    value={editData.vaccinationDate || ''}
                    onChange={handleInputChange}
                    className="border p-1 w-full"
                  />
                ) : (
                  student.vaccinationDate
                )}
              </td>
              <td className="border p-2">{student.vaccinationStatus || 'Not Vaccinated'}</td>
              <td className="border p-2">{student.bookingStatus || 'Not Booked'}</td>
              <td className="border p-2 space-x-2">
                {editingIndex === index ? (
                  <button onClick={handleSave} className="btn btn-sm btn-primary">Save</button>
                ) : (
                  <>
                    {(student.bookingStatus === null || student.bookingStatus === 'Not Approved') && (
                      <button onClick={() => handleBookDrive(index)} className="btn btn-sm btn-success">Book Drive</button>
                    )}
                    <button onClick={() => handleEditClick(index)} className="btn btn-sm btn-warning">Edit</button>
                    <button onClick={() => handleDelete(student.id)} className="btn btn-sm btn-danger">Delete</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StudentList;
