import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [totalStudents, setTotalStudents] = useState(0);
  const [vaccinatedCount, setVaccinatedCount] = useState(0);
  const [drives, setDrives] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch students
        const { data: students } = await axios.get('http://localhost:5000/students');
        setTotalStudents(students.length);
        setVaccinatedCount(students.filter(s => s.vaccinationStatus === 'Vaccinated').length);

        // Fetch vaccination drives
        const token = localStorage.getItem('token');
        const { data: drivesData } = await axios.get('http://localhost:5000/vaccination-drives', {
          headers: { Authorization: `Bearer ${token}` },
        });

        setDrives(drivesData.sort((a, b) => new Date(a.date) - new Date(b.date)));
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };

    fetchData();
  }, []);

  const vaccinatedPercentage = totalStudents ? ((vaccinatedCount / totalStudents) * 100).toFixed(2) : 0;

  return (
    <div className="container mt-4">
      <h2>Dashboard</h2>

      <div className="row mt-4">
        {[
          { label: 'Total Students', value: totalStudents },
          { label: 'Vaccinated Students', value: vaccinatedCount },
          { label: 'Vaccination %', value: `${vaccinatedPercentage}%` },
        ].map(({ label, value }, idx) => (
          <div className="col-md-4 mb-3" key={idx}>
            <div className="card p-3 text-center bg-light">
              <h5>{label}</h5>
              <h3>{value}</h3>
            </div>
          </div>
        ))}
      </div>

      <div className="card mt-5 p-3">
        <h4>Upcoming Vaccination Drives</h4>
        <table className="table table-bordered mt-3">
          <thead>
            <tr>
              <th>Vaccine</th>
              <th>Date</th>
              <th>Doses</th>
              <th>Classes</th>
            </tr>
          </thead>
          <tbody>
            {drives.length ? (
              drives.map(drive => (
                <tr key={drive.id}>
                  <td>{drive.vaccine}</td>
                  <td>{drive.date}</td>
                  <td>{drive.doses}</td>
                  <td>{drive.classes}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4" className="text-center">No drives scheduled</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
