import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid, Legend } from 'recharts';

const Dashboard = () => {
  const [totalStudents, setTotalStudents] = useState(0);
  const [vaccinatedCount, setVaccinatedCount] = useState(0);
  const [students, setStudents] = useState([]);
  const [vaccinationData, setVaccinationData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [studentsRes] = await Promise.all([
          axios.get('http://localhost:5000/students'),
        ]);

        const students = studentsRes.data;
        setStudents(students);

        const vaccinated = students.filter(s => s.vaccinationStatus === 'Vaccinated');
        setTotalStudents(students.length);
        setVaccinatedCount(vaccinated.length);

        // Create vaccination type breakdown
        const counts = {};
        vaccinated.forEach(student => {
          const vaccine = student.vaccinationName || 'Unknown';
          counts[vaccine] = (counts[vaccine] || 0) + 1;
        });

        const chartData = Object.entries(counts).map(([name, value]) => ({
          name,
          value,
        }));

        setVaccinationData(chartData);
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };

    fetchData();
  }, []);

  const vaccinatedPercentage = totalStudents
    ? ((vaccinatedCount / totalStudents) * 100).toFixed(2)
    : 0;

  return (
    <div className="container mt-4">
      <h2>Dashboard</h2>

      <div className="row mt-4">
        <div className="col-md-4 mb-3">
          <div className="card p-3 text-center bg-light">
            <h5>Total Students</h5>
            <h3>{totalStudents}</h3>
          </div>
        </div>
        <div className="col-md-4 mb-3">
          <div className="card p-3 text-center bg-light">
            <h5>Vaccinated Students</h5>
            <h3>{vaccinatedCount}</h3>
          </div>
        </div>
        <div className="col-md-4 mb-3">
          <div className="card p-3 text-center bg-light">
            <h5>Vaccination %</h5>
            <h3>{vaccinatedPercentage}%</h3>
          </div>
        </div>
      </div>

      <div className="row mt-4">
        <div className="col-md-12 text-center">
          <h4>Vaccination Progress</h4>
          <div style={{ width: 200, height: 200, margin: '0 auto' }}>
            <CircularProgressbar
              value={vaccinatedPercentage}
              text={`${vaccinatedCount} / ${totalStudents}`}
              styles={buildStyles({
                pathColor: '#4caf50',
                textColor: '#333',
                trailColor: '#d6d6d6',
                strokeWidth: 8,
                textSize: '16px',
              })}
            />
          </div>
        </div>
      </div>

      {/* Bar Chart for Vaccination Type Breakdown */}
      <div className="card mt-5 p-4">
        <h4 className="mb-4 text-center">Vaccination Breakdown by Type</h4>
        {vaccinationData.length > 0 ? (
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={vaccinationData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis allowDecimals={false} />
              <Tooltip />
              <Legend />
              <Bar dataKey="value" fill="#4caf50" />
            </BarChart>
          </ResponsiveContainer>
        ) : (
          <p className="text-center">No vaccination data available</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
