import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Papa from 'papaparse';

const Reports = () => {
  const [students, setStudents] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [filters, setFilters] = useState({
    name: '',
    class: '',
    studentId: '',
    vaccinationName: '',
    vaccinationDate: '',
    vaccinationStatus: ''
  });
  const [currentPage, setCurrentPage] = useState(1);
  const studentsPerPage = 5;

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const res = await axios.get('http://localhost:5000/students');
      setStudents(res.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const handleGenerate = () => {
    const result = students.filter((s) => {
      return (
        (filters.name ? s.name.toLowerCase().includes(filters.name.toLowerCase()) : true) &&
        (filters.class ? s.class.toLowerCase().includes(filters.class.toLowerCase()) : true) &&
        (filters.studentId ? s.studentId.toLowerCase().includes(filters.studentId.toLowerCase()) : true) &&
        (filters.vaccinationName ? s.vaccinationName?.toLowerCase().includes(filters.vaccinationName.toLowerCase()) : true) &&
        (filters.vaccinationDate
          ? new Date(s.vaccinationDate).toISOString().split('T')[0] === filters.vaccinationDate
          : true) &&
        (filters.vaccinationStatus
          ? s.vaccinationStatus?.toLowerCase().includes(filters.vaccinationStatus.toLowerCase())
          : true)
      );
    });
    setFiltered(result);
    setCurrentPage(1);
  };

  const downloadCSV = () => {
    const csv = Papa.unparse(filtered);
    const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);

    const link = document.createElement('a');
    link.href = url;
    link.setAttribute('download', 'vaccination_report.csv');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const indexOfLast = currentPage * studentsPerPage;
  const indexOfFirst = indexOfLast - studentsPerPage;
  const currentStudents = filtered.slice(indexOfFirst, indexOfLast);
  const totalPages = Math.ceil(filtered.length / studentsPerPage);

  return (
    <div>
      <h3>Vaccination Reports</h3>

      <div style={{ marginBottom: '1rem' }}>
        <input name="name" placeholder="Name" onChange={handleFilterChange} />
        <input name="class" placeholder="Class" onChange={handleFilterChange} />
        <input name="studentId" placeholder="Student ID" onChange={handleFilterChange} />
        <input name="vaccinationName" placeholder="Vaccine Name" onChange={handleFilterChange} />
        <input name="vaccinationDate" type="date" onChange={handleFilterChange} />
        <input name="vaccinationStatus" placeholder="Vaccination Status" onChange={handleFilterChange} />
        <button onClick={handleGenerate}>Generate Report</button>
        {filtered.length > 0 && <button onClick={downloadCSV}>Download CSV</button>}
      </div>

      {filtered.length > 0 ? (
        <>
          <table border="1" cellPadding="10">
            <thead>
              <tr>
                <th>Name</th>
                <th>Class</th>
                <th>Student ID</th>
                <th>Vaccine Name</th>
                <th>Vaccination Date</th>
                <th>Vaccination Status</th>
                <th>Booking Status</th>
              </tr>
            </thead>
            <tbody>
              {currentStudents.map((s, i) => (
                <tr key={i}>
                  <td>{s.name}</td>
                  <td>{s.class}</td>
                  <td>{s.studentId}</td>
                  <td>{s.vaccinationName}</td>
                  <td>{s.vaccinationDate ? new Date(s.vaccinationDate).toISOString().split('T')[0] : 'N/A'}</td>
                  <td>{s.vaccinationStatus}</td>
                  <td>{s.bookingStatus}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <div style={{ marginTop: '1rem' }}>
            <button disabled={currentPage === 1} onClick={() => setCurrentPage((p) => p - 1)}>Prev</button>
            <span> Page {currentPage} of {totalPages} </span>
            <button disabled={currentPage === totalPages} onClick={() => setCurrentPage((p) => p + 1)}>Next</button>
          </div>
        </>
      ) : (
        <p>No data found. Please adjust filters and try again.</p>
      )}
    </div>
  );
};

export default Reports;
