import React, { useState } from 'react';

const ManageStudents = () => {
  const [students, setStudents] = useState([
    { name: 'John Doe', class: '5', studentId: '1001', vaccinationStatus: 'Not Vaccinated', vaccinationName: 'Polio' },
    { name: 'Jane Smith', class: '6', studentId: '1002', vaccinationStatus: 'Vaccinated', vaccinationName: 'MMR' },
    { name: 'Alice Johnson', class: '5', studentId: '1003', vaccinationStatus: 'Not Vaccinated', vaccinationName: 'Hepatitis B' },
    { name: 'Bob Brown', class: '7', studentId: '1004', vaccinationStatus: 'Vaccinated', vaccinationName: 'Polio' },
  ]);

  const [editingIndex, setEditingIndex] = useState(null);
  const [editData, setEditData] = useState({});
  const [searchTerm, setSearchTerm] = useState('');

  const handleEditClick = (index) => {
    setEditingIndex(index);
    setEditData(students[index]);
  };

  const handleSave = () => {
    const updatedStudents = [...students];
    updatedStudents[editingIndex] = editData;
    setStudents(updatedStudents);
    setEditingIndex(null);
    setEditData({});
  };

  const handleDelete = (index) => {
    const updated = students.filter((_, i) => i !== index);
    setStudents(updated);
  };

  const handleVaccinationStatusChange = () => {
    if (editData.vaccinationStatus === 'Not Vaccinated') {
      setEditData({ ...editData, vaccinationStatus: 'Vaccinated' });
    }
  };

  const filteredStudents = students.filter((student) => {
    const term = searchTerm.toLowerCase();
    return (
      student.name.toLowerCase().includes(term) ||
      student.studentId.toLowerCase().includes(term) ||
      student.vaccinationName.toLowerCase().includes(term)
    );
  });

  return (
    <div className="p-8 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Manage Students</h1>

      {/* Search */}
      <div className="mb-4">
        <input
          type="text"
          placeholder="Search by name, student ID, or vaccination name"
          className="border p-2 rounded w-full"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Student Table */}
      <div>
        <h2 className="text-xl font-semibold mb-4">Student List</h2>
        <table className="w-full border-collapse border">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2 text-left">Name</th>
              <th className="border p-2 text-left">Class</th>
              <th className="border p-2 text-left">Student ID</th>
              <th className="border p-2 text-left">Vaccination Name</th>
              <th className="border p-2 text-left">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredStudents.map((student, index) => (
              <React.Fragment key={index}>
                <tr>
                  {editingIndex === index ? (
                    <>
                      <td className="border p-2">
                        <input
                          value={editData.name}
                          onChange={(e) => setEditData({ ...editData, name: e.target.value })}
                          className="border p-1 rounded w-full"
                        />
                      </td>
                      <td className="border p-2">
                        <input
                          value={editData.class}
                          onChange={(e) => setEditData({ ...editData, class: e.target.value })}
                          className="border p-1 rounded w-full"
                        />
                      </td>
                      <td className="border p-2">
                        <input
                          value={editData.studentId}
                          onChange={(e) => setEditData({ ...editData, studentId: e.target.value })}
                          className="border p-1 rounded w-full"
                        />
                      </td>
                      <td className="border p-2">
                        <input
                          value={editData.vaccinationName}
                          onChange={(e) => setEditData({ ...editData, vaccinationName: e.target.value })}
                          className="border p-1 rounded w-full"
                        />
                      </td>
                      <td className="border p-2 space-x-2">
                        <button onClick={handleSave} className="bg-green-500 text-white px-2 py-1 rounded">Save</button>
                        <button onClick={() => setEditingIndex(null)} className="bg-gray-500 text-white px-2 py-1 rounded">Cancel</button>
                      </td>
                    </>
                  ) : (
                    <>
                      <td className="border p-2">{student.name}</td>
                      <td className="border p-2">{student.class}</td>
                      <td className="border p-2">{student.studentId}</td>
                      <td className="border p-2">{student.vaccinationName}</td>
                      <td className="border p-2 space-x-2">
                        <button
                          onClick={() => handleEditClick(index)}
                          className="bg-blue-500 text-white px-2 py-1 rounded"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDelete(index)}
                          className="bg-red-500 text-white px-2 py-1 rounded"
                        >
                          Delete
                        </button>
                      </td>
                    </>
                  )}
                </tr>

                {/* Vaccination Status Row */}
                <tr>
                  <td colSpan="5" className="border p-2 bg-gray-50">
                    <strong>Vaccination Status:</strong>{' '}
                    {editingIndex === index ? (
                      editData.vaccinationStatus === 'Vaccinated' ? (
                        <span className="text-green-600 font-semibold">Vaccinated (Locked)</span>
                      ) : (
                        <>
                          <span className="mr-4">{editData.vaccinationStatus}</span>
                          <button
                            onClick={handleVaccinationStatusChange}
                            className="bg-yellow-500 text-white px-2 py-1 rounded"
                          >
                            Mark as Vaccinated
                          </button>
                        </>
                      )
                    ) : (
                      <span className={student.vaccinationStatus === 'Vaccinated' ? 'text-green-600 font-semibold' : 'text-red-600'}>
                        {student.vaccinationStatus}
                      </span>
                    )}
                  </td>
                </tr>
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManageStudents;
