import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Reports = () => {
  const [pendingBookings, setPendingBookings] = useState([]);

  const fetchPendingBookings = async () => {
    try {
      const res = await axios.get('http://localhost:5000/students');
      const pending = res.data.filter((student) => student.bookingStatus === 'Pending');
      setPendingBookings(pending);
    } catch (err) {
      console.error('Error fetching pending bookings:', err);
    }
  };

  const handleApprove = async (id) => {
    try {
      // Find the student from the state
      const student = pendingBookings.find((s) => s.id === id);

      // Send full data back to avoid nullifying others
      await axios.put(`http://localhost:5000/vaccination/${id}`, {
        name: student.name,
        class: student.class,
        studentId: student.studentId,
        vaccinationName: student.vaccinationName,
        vaccinationDate: student.vaccinationDate,
        bookingStatus: 'Approved',
      });

      fetchPendingBookings(); // Refresh list
    } catch (err) {
      console.error('Error approving booking:', err);
    }
  };

  useEffect(() => {
    fetchPendingBookings();
  }, []);

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h3 className="text-2xl font-bold mb-4">Reports</h3>
      <p className="mb-6">View statistics and generate reports here.</p>

      <h4 className="text-xl font-semibold mb-2">Pending Bookings</h4>
      {pendingBookings.length === 0 ? (
        <p className="text-gray-600">No pending bookings found.</p>
      ) : (
        <table className="w-full border-collapse border">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2">Name</th>
              <th className="border p-2">Class</th>
              <th className="border p-2">Student ID</th>
              <th className="border p-2">Vaccination</th>
              <th className="border p-2">Date</th>
              <th className="border p-2">Booking Status</th>
              <th className="border p-2">Action</th>
            </tr>
          </thead>
          <tbody>
            {pendingBookings.map((student) => (
              <tr key={student.id}>
                <td className="border p-2">{student.name}</td>
                <td className="border p-2">{student.class}</td>
                <td className="border p-2">{student.studentId}</td>
                <td className="border p-2">{student.vaccinationName}</td>
                <td className="border p-2">
                  {student.vaccinationDate
                    ? new Date(student.vaccinationDate).toLocaleDateString()
                    : '—'}
                </td>
                <td className="border p-2">{student.bookingStatus}</td>
                <td className="border p-2">
                  <button
                    onClick={() => handleApprove(student.id)}
                    className="bg-green-600 text-white px-3 py-1 rounded"
                  >
                    Approve
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Reports;
