import StudentRow from './StudentRow';

const StudentTable = ({
  students = [],
  filteredStudents = [],
  editingIndex,
  bookingIndex,
  editData,
  setEditData,
  setEditingIndex,
  setBookingIndex,
  handleEditClick,
  handleBookClick,
  handleSave,
  handleVaccinationSave,
  handleDelete,
  handleMarkAsVaccinated
}) => {
  return (
    <table className="w-full border-collapse border">
      <thead>
        <tr className="bg-gray-100">
          <th className="border p-2">Name</th>
          <th className="border p-2">Class</th>
          <th className="border p-2">Student ID</th>
          <th className="border p-2">Booking Status</th>
          <th className="border p-2">Vaccination</th>
          <th className="border p-2">Date</th>
          <th className="border p-2">Status</th>
          <th className="border p-2">Actions</th>
        </tr>
      </thead>
      <tbody>
        {(filteredStudents || []).map((student, index) => (
          <StudentRow
            key={student.id}
            student={student}
            index={index}
            isEditing={editingIndex === index}
            isBooking={bookingIndex === index}
            editData={editData}
            setEditData={setEditData}
            setEditingIndex={setEditingIndex}
            setBookingIndex={setBookingIndex}
            handleEditClick={handleEditClick}
            handleBookClick={handleBookClick}
            handleSave={handleSave}
            handleVaccinationSave={handleVaccinationSave}
            handleDelete={handleDelete}
            handleMarkAsVaccinated={handleMarkAsVaccinated}
          />
        ))}
      </tbody>
    </table>
  );
};

export default StudentTable;
