import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
  Legend,
} from 'recharts';

const Dashboard = () => {
  const [totalStudents, setTotalStudents] = useState(0);
  const [vaccinatedCount, setVaccinatedCount] = useState(0);
  const [vaccineStats, setVaccineStats] = useState([]);
  const [drives, setDrives] = useState([]);
  const [selectedDrive, setSelectedDrive] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [studentsRes, drivesRes] = await Promise.all([
          axios.get('http://localhost:5000/students'),
          axios.get('http://localhost:5000/vaccination-drives', {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
          }),
        ]);

        const students = studentsRes.data;
        const vaccinated = students.filter(s => s.vaccinationStatus === 'Vaccinated');

        setTotalStudents(students.length);
        setVaccinatedCount(vaccinated.length);

        // Vaccine breakdown calculation
        const vaccineMap = {};
        vaccinated.forEach(student => {
          const name = student.vaccinationName;
          if (name) {
            vaccineMap[name] = (vaccineMap[name] || 0) + 1;
          }
        });
        const statsArray = Object.entries(vaccineMap).map(([vaccine, count]) => ({ vaccine, count }));
        setVaccineStats(statsArray);

        //const sortedDrives = drivesRes.data.sort((a, b) => new Date(a.date) - new Date(b.date));
        const sortedDrives = drivesRes.data
  .filter(drive => {
    const today = new Date();
    const driveDate = new Date(drive.date);
    const timeDiff = driveDate - today;
    const daysDiff = timeDiff / (1000 * 60 * 60 * 24);
    return daysDiff >= 0 && daysDiff <= 30;
  })
  .sort((a, b) => new Date(a.date) - new Date(b.date));
setDrives(sortedDrives);
        setDrives(sortedDrives);
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };

    fetchData();
  }, []);

  const vaccinatedPercentage = totalStudents
    ? ((vaccinatedCount / totalStudents) * 100).toFixed(2)
    : 0;

  return (
    <div className="container mt-4">
      <h2>Dashboard</h2>

      <div className="row mt-4">
        <div className="col-md-4 mb-3">
          <div className="card p-3 text-center bg-light">
            <h5>Total Students</h5>
            <h3>{totalStudents}</h3>
          </div>
        </div>
        <div className="col-md-4 mb-3">
          <div className="card p-3 text-center bg-light">
            <h5>Vaccinated Students</h5>
            <h3>{vaccinatedCount}</h3>
          </div>
        </div>
        <div className="col-md-4 mb-3">
          <div className="card p-3 text-center bg-light">
            <h5>Vaccination %</h5>
            <h3>{vaccinatedPercentage}%</h3>
          </div>
        </div>
      </div>

      <div className="row mt-4">
        <div className="col-md-12 text-center">
          <h4>Vaccination Progress</h4>
          <div style={{ width: 200, height: 200, margin: '0 auto' }}>
            <CircularProgressbar
              value={vaccinatedPercentage}
              text={`${vaccinatedCount} / ${totalStudents}`}
              styles={buildStyles({
                pathColor: '#4caf50',
                textColor: '#333',
                trailColor: '#d6d6d6',
                strokeWidth: 8,
                textSize: '16px',
              })}
            />
          </div>
        </div>
      </div>

      {/* Vaccine Breakdown Graph */}
      <div className="card mt-5 p-4">
        <h4 className="mb-4 text-center">Vaccination Breakdown by Type</h4>
        {vaccineStats.length > 0 ? (
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={vaccineStats}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="vaccine" />
              <YAxis allowDecimals={false} />
              <Tooltip />
              <Legend />
              <Bar dataKey="count" fill="#4caf50" />
            </BarChart>
          </ResponsiveContainer>
        ) : (
          <p className="text-muted text-center">No vaccination data available</p>
        )}
      </div>

      {/* Vaccination Drives */}
      <div className="card mt-5 p-3">
        <h4>Upcoming Vaccination Drives</h4>
        <table className="table table-bordered mt-3">
          <thead>
            <tr>
              <th>Vaccine</th>
              <th>Date</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {drives.length > 0 ? (
              drives.map((drive) => (
                <tr key={drive.id}>
                  <td>{drive.vaccine}</td>
                  <td>{new Date(drive.date).toLocaleDateString()}</td>
                  <td>
                    <button
                      className="btn btn-primary btn-sm"
                      onClick={() => {
                        setSelectedDrive(drive);
                        setShowModal(true);
                      }}
                    >
                      View More
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3" className="text-center">No drives scheduled</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>



      {/* Modal */}
      {showModal && selectedDrive && (
        <div
          className="modal fade show"
          style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}
          tabIndex="-1"
        >
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  Drive Details - {selectedDrive.vaccine}
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                <p><strong>Date:</strong> {selectedDrive.date}</p>
                <p><strong>Doses:</strong> {selectedDrive.doses}</p>
                <p><strong>Classes:</strong> {selectedDrive.classes}</p>
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setShowModal(false)}
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
