import React, { useState } from 'react';

const ManageStudents = () => {
  const [students, setStudents] = useState([
    { name: 'John Doe', class: '5', studentId: '1001', vaccinationStatus: 'Not Vaccinated', vaccinationName: 'Polio' },
    { name: 'Jane Smith', class: '6', studentId: '1002', vaccinationStatus: 'Vaccinated', vaccinationName: 'MMR' },
    { name: 'Alice Johnson', class: '5', studentId: '1003', vaccinationStatus: 'Not Vaccinated', vaccinationName: 'Hepatitis B' },
    { name: 'Bob Brown', class: '7', studentId: '1004', vaccinationStatus: 'Vaccinated', vaccinationName: 'Polio' },
  ]);

  const [searchTerm, setSearchTerm] = useState('');

  const toggleVaccinationStatus = (index) => {
    const updatedStudents = [...students];
    if (updatedStudents[index].vaccinationStatus === 'Not Vaccinated') {
      updatedStudents[index].vaccinationStatus = 'Vaccinated';
      setStudents(updatedStudents);
    }
  };

  const filteredStudents = students.filter((student) => {
    const term = searchTerm.toLowerCase();
    return (
      student.name.toLowerCase().includes(term) ||
      student.studentId.toLowerCase().includes(term) ||
      student.vaccinationName.toLowerCase().includes(term)
    );
  });

  return (
    <div className="p-8 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Add/Manage Student Details</h1>

      {/* Add Student Form */}
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Add Student</h2>
        <div className="grid grid-cols-2 gap-4">
          <input type="text" placeholder="Name" className="border p-2 rounded" />
          <select className="border p-2 rounded text-gray-500">
            <option>Select Class</option>
            <option value="5">5</option>
            <option value="6">6</option>
            <option value="7">7</option>
          </select>
          <input type="text" placeholder="Student ID" className="border p-2 rounded" />
          <select className="border p-2 rounded text-gray-500">
            <option>Select Status</option>
            <option>Vaccinated</option>
            <option>Not Vaccinated</option>
          </select>
          <input type="text" placeholder="Vaccination Name" className="border p-2 rounded col-span-2" />
        </div>
        <button className="bg-blue-600 text-white px-4 py-2 rounded mt-4">Add</button>
      </div>

      {/* Bulk Upload */}
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-2">Bulk Upload</h2>
        <input type="file" className="border p-2 rounded" />
      </div>

      {/* Search */}
      <div className="mb-4">
        <input
          type="text"
          placeholder="Search by name, student ID, or vaccination name"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="border p-2 rounded w-full"
        />
      </div>

      {/* Student List */}
      <div>
        <h2 className="text-xl font-semibold mb-4">Student List</h2>
        <table className="w-full border-collapse border">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2 text-left">Name</th>
              <th className="border p-2 text-left">Class</th>
              <th className="border p-2 text-left">Student ID</th>
              <th className="border p-2 text-left">Vaccination Status</th>
              <th className="border p-2 text-left">Vaccination Name</th>
              <th className="border p-2 text-left">Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredStudents.length > 0 ? (
              filteredStudents.map((student, idx) => (
                <tr key={idx}>
                  <td className="border p-2">{student.name}</td>
                  <td className="border p-2">{student.class}</td>
                  <td className="border p-2">{student.studentId}</td>
                  <td className="border p-2">{student.vaccinationStatus}</td>
                  <td className="border p-2">{student.vaccinationName}</td>
                  <td className="border p-2">
                    {student.vaccinationStatus === 'Not Vaccinated' ? (
                      <button
                        onClick={() => toggleVaccinationStatus(idx)}
                        className="bg-yellow-500 text-white px-2 py-1 rounded"
                      >
                        Mark as Vaccinated
                      </button>
                    ) : (
                      <span className="text-green-600 font-semibold">Already Vaccinated</span>
                    )}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" className="border p-2 text-center text-gray-500">
                  No students found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManageStudents;
